INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '불편한 편의점', '김호연', '소설/시/에세이', '한국소설', '12,600', '나무옆의자', 1000, TO_DATE('2021년 04월', 'YYYY"년" MM"월"'), 1000, '불편한데 자꾸 가고 싶은 편의점이 있다! 힘들게 살아낸 오늘을 위로하는 편의점의 밤정체불명의 알바로부터 시작된 웃음과 감동의 나비효과『망원동 브라더스』 김호연의 ‘동네 이야기’ 시즌 22013년 세계문학상 우수상 수상작 『망원동 브라더스』로 데뷔한 후 일상적 현실을 위트 있게 그린 경쾌한 작품과 인간의 내밀...', '불편한 편의점.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '불편한 편의점 2', '김호연', '소설/시/에세이', '한국소설', '12,600', '나무옆의자', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '재방문을 환영합니다 여기는 청파동 ALWAYS편의점입니다출간 후 1년이 넘도록 독자의 사랑을 받으며 베스트셀러 상위권을 지키고 있는 소설, 김호연 작가의 『불편한 편의점』이 그 두 번째 이야기로 다시 찾아왔다. 청파동 골목의 작은 편의점을 무대로 힘겨운 시대를 살아가는 우리 이웃들의 삶을 따뜻하고 유쾌하게 그린...', '불편한 편의점 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아버지의 해방일지', '정지아', '소설/시/에세이', '한국소설', '13,500', '창비', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '새삼스럽게 경탄스럽다!압도적인 몰입감, 가슴 먹먹한 감동정지아의 손끝에서 펼쳐지는 시대의 온기미스터리 같은 한 남자가 헤쳐온 역사의 격랑그 안에서 발견하는 끝끝내 강인한 우리의 인생김유정문학상 심훈문학대상 이효석문학상 등을 수상하며 문학성을 두루 입증받은 ‘리얼리스트’ 정지아가 무려 32년 만에 장편소...', '아버지의 해방일지.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '하얼빈', '김훈', '소설/시/에세이', '한국소설', '14,400', '문학동네', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '『칼의 노래』를 넘어서는 깊이와 감동김훈이 반드시 써내야만 했던 일생의 과업 ‘우리 시대 최고의 문장가’ ‘작가들의 작가’로 일컬어지는 소설가 김훈의 신작 장편소설 『하얼빈』이 출간되었다. 『하얼빈』은 김훈이 작가로 활동하는 내내 인생 과업으로 삼아왔던 특별한 작품이다. 작가는 청년 시절부터 안중근의 ...', '하얼빈.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '메리골드 마음 세탁소', '윤정은', '소설/시/에세이', '한국소설', '13,500', '북로망스', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '마음에 묻은 얼룩을 마법처럼 지워드립니다 윤정은 작가 등단 11년 만의 첫 장편소설 한국형 힐링 소설의 결정판우리는 가끔 시간을 되돌려 과거로 돌아가 후회됐던 일을 되돌리고 싶어한다. 그런데 과연 그 일을 지워버리는 게 현명한 선택일까? 그리고 그 기억을 지웠을 때 지금의 내가 있을 수 있을까? 만약 그 기억만 ...', '메리골드 마음 세탁소.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '작별인사', '김영하', '소설/시/에세이', '한국소설', '12,600', '복복서가', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '누구도 도와줄 수 없는 상황, 혼자 헤쳐나가야 한다지켜야 할 약속, 붙잡고 싶은 온기김영하가 『살인자의 기억법』 이후 9 년 만에 내놓는 장편소설 『작별인사』는 그리 멀지 않은 미래를 배경으로, 별안간 삶이 송두리째 뒤흔들린 한 소년의 여정을 좇는다. 유명한 IT 기업의 연구원인 아버지와 쾌적하고 평화롭게 살아...', '작별인사.jpg');
