INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '달러구트 꿈 백화점', '이미예', '소설/시/에세이', '한국소설', '12,420', '팩토리나인', 1000, TO_DATE('2020년 07월', 'YYYY"년" MM"월"'), 1000, '잠들어야만 입장할 수 있는 독특한 마을. 그곳에 들어온 잠든 손님들에게 가장 인기 있는 곳은, 온갖 꿈을 한데 모아 판매하는 ‘달러구트의 꿈 백화점’이다. 긴 잠을 자는 사람들은 물론이고, 짧은 낮잠을 자는 사람들과 동물들로 매일매일 대성황을 이룬다. 범상치 않은 혈통의 주인장 ‘달러구트’, 그리고 그의 최측근...', '달러구트 꿈 백화점.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '달러구트 꿈 백화점 2 (레인보우 에디션)', '이미예', '소설/시/에세이', '한국소설', '12,420', '팩토리나인', 1000, TO_DATE('2021년 07월', 'YYYY"년" MM"월"'), 1000, '100만 독자를 사로잡은 『달러구트 꿈 백화점』, 그 두 번째 이야기어느덧 페니가 달러구트 꿈 백화점에서 일한 지도 1년이 넘었다. 재고가 부족한 꿈을 관리하고, 꿈값 창고에서 감정으로 가득 찬 병을 옮기고, 프런트의 수많은 눈꺼풀 저울을 관리하는 일에 능숙해진 페니는 자신감이 넘친다. 게다가 꿈 산업 종사자로 인...', '달러구트 꿈 백화점 2 (레인보우 에디션).jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '모순', '양귀자', '소설/시/에세이', '한국소설', '11,700', '쓰다', 1000, TO_DATE('2013년 04월', 'YYYY"년" MM"월"'), 1000, '초판이 나온 지 벌써 15년이 흘렀지만 이 소설 『모순』은 아주 특별한 길을 걷고 있다. 그때 20대였던 독자들은 지금 결혼을 하고 30대가 되어서도 가끔씩 『모순』을 꺼내 다시 읽는다고 했다. 다시 읽을 때마다 전에는 몰랐던 소설 속 행간의 의미를 깨우치거나 세월의 힘이 알려준 다른 해석에 놀라면서 내 인생의 가...', '모순.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '구의 증명', '최진영', '소설/시/에세이', '한국소설', '7,200', '은행나무', 1000, TO_DATE('2015년 03월', 'YYYY"년" MM"월"'), 1000, '만약 네가 먼저 죽는다면나는 너를 먹을 거야.그래야 너 없이도 죽지 않고 살 수 있어.사랑 후 남겨진 것들에 관한 숭고할 만큼 아름다운 이야기2006년 계간《실천문학》으로 등단한 뒤 2010년 《당신 옆을 스쳐간 그 소녀의 이름은》으로 한겨레문학상을 수상한 최진영의 소설 《구의 증명》이 은행나무에서 출간되었다. ...', '구의 증명.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '지구 끝의 온실', '김초엽', '소설/시/에세이', '한국소설', '13,500', '자이언트북스', 1000, TO_DATE('2021년 08월', 'YYYY"년" MM"월"'), 1000, '김초엽 첫 장편소설, 모두가 간절히 기다려온 이야기 이미 폭넓은 독자층을 형성하며 열렬한 사랑을 받고 있는 김초엽 작가는 더스트로 멸망한 이후의 세계를 첫 장편소설의 무대로 삼았다. 그는 지난해 말 플랫폼 연재를 통해 발표한 이야기를 반년이 훌쩍 넘는 시간 동안 수정하면서 한층 더 무르익도록 만들었다. 그리하...', '지구 끝의 온실.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이토록 평범한 미래', '김연수', '소설/시/에세이', '한국소설', '12,600', '문학동네', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '작가 김연수가 짧지 않은 침묵을 깨고 신작 소설집 『이토록 평범한 미래』를 출간한다. 『사월의 미, 칠월의 솔』(2013) 이후 9년 만에 펴내는 여섯번째 소설집이다. 그전까지 2~4년 간격으로 꾸준히 소설집을 펴내며 ‘다작 작가’로 알려져온 그에게 지난 9년은 바뀌어야 한다는 내적인 욕구가 강하게 작동하는 동시...', '이토록 평범한 미래.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '밝은 밤', '최은영', '소설/시/에세이', '한국소설', '13,050', '문학동네', 1000, TO_DATE('2021년 07월', 'YYYY"년" MM"월"'), 1000, '백 년의 시간을 감싸안으며 이어지는 사랑과 숨의 기록 『쇼코의 미소』 『내게 무해한 사람』 최은영 첫 장편소설공감을 불러일으키는 이야기와 서정적이며 사려 깊은 문장, 그리고 그 안에 자리한 뜨거운 문제의식으로 등단 이후 줄곧 폭넓은 독자의 지지와 문학적 조명을 두루 받고 있는 작가 최은영의 첫 장편소설. ‘...', '밝은 밤.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '칵테일, 러브, 좀비', '조예은', '소설/시/에세이', '한국소설', '9,000', '안전가옥', 1000, TO_DATE('2020년 04월', 'YYYY"년" MM"월"'), 1000, '안전가옥 쇼-트 시리즈의 두 번째 책으로, 조예은 작가의 단편집이다. 안전가옥 오리지널 시리즈의 첫 책 『뉴서울파크 젤리장수 대학살』에서 탄탄한 구성의 호러 스릴러를 선보였던 작가의 연출력은 단편집에서 더욱 다양한 색채로 빛을 발한다. 미묘하지만 분명한 폭력을 감내해 왔던 여성 빌런의 탄생을 그린 「초대」,...', '칵테일, 러브, 좀비.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아몬드', '손원평', '소설/시/에세이', '한국소설', '12,600', '창비', 1000, TO_DATE('2017년 03월', 'YYYY"년" MM"월"'), 1000, '감정을 느끼지 못하는 소년의 특별한 성장 이야기. 타인의 감정에 무감각해진 공감 불능인 이 시대에 큰 울림을 주는 소설로, 작품 속 인물들이 타인과 관계 맺고 슬픔에 공감하며 성장해 나가는 과정을 탁월하게 묘사했다. 『완득이』, 『위저드 베이커리』를 잇는 제10회 창비청소년문학상 수상작.', '아몬드.jpg');
