INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '튜브', '손원평', '소설/시/에세이', '한국소설', '13,500', '창비', 1000, TO_DATE('2022년 07월', 'YYYY"년" MM"월"'), 1000, '100만부 베스트셀러 『아몬드』 작가 손원평의 빛나는 신작 모두의 인생을 향해 보내는 강력한 응원화제의 데뷔작 『아몬드』(창비 2017)로 100만 독자의 사랑을 받은 작가 손원평의 신작 장편소설 『튜브』가 출간되었다. 손원평은 ‘믿고 읽는’ 작가라는 타이틀을 넘어서서 이미 전세계, 전세대 독자들에게 널리 읽히며 ...', '튜브.jpg');
