INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '사람은 무엇으로 사는가', '레프 톨스토이 홍대화', '소설/시/에세이', '고전문학', '6,930', '현대지성', 1000, TO_DATE('2021년 02월', 'YYYY"년" MM"월"'), 1000, '자살 직전까지 갔던 톨스토이의 삶을 통째로 바꾼 삶의 진리… 그 깨달음을 오롯이 녹여낸 명품 단편 모음!위대한 소설가로 세계적인 명성을 누렸던 시절에도 채울 수 없었던 톨스토이의 마음을 만족하게 한 것은 무엇이었을까? 그 깨달음으로 살아갈 이유를 찾은 후 작품의 색깔까지 완전히 바꾸어 집필한 10편의 명 단편...', '사람은 무엇으로 사는가.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '모비 딕', '허먼 멜빌 김석희', '소설/시/에세이', '고전문학', '19,800', '작가정신', 1000, TO_DATE('2011년 05월', 'YYYY"년" MM"월"'), 1000, '서두에서부터 ‘고래’에 대한 ‘어원’ 탐구와 문헌 ‘발췌록’이 등장하고, 작가의 체험과 도서관에서 조사하고 연구한 고래와 포경에 대한 갖가지 지식이 총망라된 이 독특한 소설은 출간 당시에는 어렵고 낯설다는 이유로 외면당했지만 작가가 죽고 30여 년 후에 재평가되기 시작했고 오늘날 미국문학을 대표하는 걸작...', '모비 딕.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '초판본 하늘과 바람과 별과 詩 : 윤동주 유고시집', '윤동주', '소설/시/에세이', '고전문학', '8,820', '소와다리', 1000, TO_DATE('2016년 01월', 'YYYY"년" MM"월"'), 1000, '별이 된 유고시집 [하늘과 바람과 별과 詩] 1955년 10주기 기념 증보판죽는 날까지 하늘을 우르러 한 점 부끄럼이 없기를...일본 유학 중 독립운동 및 한글창작 혐의로 체포, 조사 과정에서 이를 부인하지 않고 옥중에서 요절한 민족시인 청년 윤동주. 직접 지은 시처럼 짧지만 한 점 부끄럼 없는 삶을 살다 그는 끝내 ...', '초판본 하늘과 바람과 별과 詩 : 윤동주 유고시집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '톨스토이 단편선', '레프 톨스토이 김은경', '소설/시/에세이', '고전문학', '8,100', '인디북', 1000, TO_DATE('2005년 12월', 'YYYY"년" MM"월"'), 1000, '세계적 문호 톨스토이의 단편 모음집. 톨스토이의 인생관, 철학이 담겨져 있는 대표 단편작 사람은 무엇으로 사는가 외 바보 이반의 이야기, 사람에겐 얼마만큼의 땅이 필요한가 등의 작품이 수록되어 있다....', '톨스토이 단편선.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '셰익스피어 4대 비극 컬렉션 세트', '윌리엄 셰익스피어', '소설/시/에세이', '고전문학', '20,520', '민음사', 1000, TO_DATE('2012년 11월', 'YYYY"년" MM"월"'), 1000, '인간의 고통에 대한 가장 원숙하고도 냉혹한 성찰, 민음사의 세계문학전집 셰익스피어 4대 비극을 세트로 묶었다. 존재의 비극을 탐색한 극문학의 정수 『햄릿』, 모든 시대를 막론하고 가장 현대적인 작품 『오셀로』, 셰익스피어 4대 비극 중 가장 화려하고 잔인한 작품 『맥베스』, 소포클레스의 『안티고네』와 비견되...', '셰익스피어 4대 비극 컬렉션 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 1', '요한 볼프강 폰 괴테', '소설/시/에세이', '고전문학', '8,550', '민음사', 1000, TO_DATE('1999년 03월', 'YYYY"년" MM"월"'), 1000, '괴테 탄생 250주년을 맞이하여 민음사에서 펴낸 괴테 특별선 중 파우스트 부분. 전 2권으로 구성되어 있다. 자연 연구가, 미술가였으며, 자신의 공국에 봉사한 정치 지도자이기도 했던 괴테. 후세에는 자유에의 의무, 낯설고 이질적인 것에 대한 긍정심, 존재하는 것에 대한 감사함. 그리고 장래의 것에 대한 비판적 개방성...', '파우스트 1.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '안나 카레니나 세트', '레프 톨스토이', '소설/시/에세이', '고전문학', '27,720', '민음사', 1000, TO_DATE('2012년 11월', 'YYYY"년" MM"월"'), 1000, '19세기 러시아가 낳은 위대한 예술가 톨스토이.그가 남긴 최고의 리얼리즘 소설19세기 러시아의 대문호 레프 톨스토이의 『안나 카레니나』(Анна Каренина)가 민음사 세계문학전집 세트로 출간되었다. 동시대 작가인 도스토예프스키로부터 완벽한 예술 작품이라는 평가와, 역시 러시아 출신 소설가인 나보...', '안나 카레니나 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 2', '요한 볼프강 폰 괴테', '소설/시/에세이', '고전문학', '9,900', '민음사', 1000, TO_DATE('1999년 03월', 'YYYY"년" MM"월"'), 1000, '괴테 탄생 250주년을 맞이하여 민음사에서 펴낸 괴테 특별선 중 파우스트 부분. 전 2권으로 구성되어 있다. 자연 연구가, 미술가였으며, 자신의 공국에 봉사한 정치 지도자이기도 했던 괴테. 후세에는 자유에의 의무, 낯설고 이질적인 것에 대한 긍정심, 존재하는 것에 대한 감사함. 그리고 장래의 것에 대한 비판적 개방성...', '파우스트 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '햄릿', '윌리엄 셰익스피어 최종철', '소설/시/에세이', '고전문학', '6,300', '민음사', 1000, TO_DATE('2001년 02월', 'YYYY"년" MM"월"'), 1000, '셰익스피어 4대 비극 중 하나. 예민한 감수성과 지성, 섬세하고 결백한 성격의 소유자 햄릿은 어느 날 존경하던 아버지를 잃고, 어머니까지 숙부와 재혼함으로써 큰 충격을 받고 인생 자체에 회의를 느끼게 된다. 그러던 중 아버지의 유령이 나타나 자신이 동생에게 살해되었다는 사실을 밝히자 햄릿은 복수의 일념에 사로...', '햄릿.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '신곡', '단테 김운찬', '소설/시/에세이', '고전문학', '32,400', '열린책들', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '귀스타브 도레의 삽화를 모두 수록한김운찬 교수의 이탈리아어 완역본귀스타브 도레의 아름다운 삽화를 수록한 단테 알리기에리의 『신곡』 개정판. 이번 개정판은 대구가톨릭대학교 프란치스코칼리지의 김운찬 교수가 번역하여 2007년 출간한 이탈리아어 완역본 『신곡』을 번역과 편집, 디자인을 모두 새롭게 손보아 제작...', '신곡.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 세트', '요한 볼프강 폰 괴테 전영애', '소설/시/에세이', '고전문학', '54,000', '길', 1000, TO_DATE('2019년 06월', 'YYYY"년" MM"월"'), 1000, '세상을 해석하고 바꾸는 데 바탕이 되는 인문사회과학 분야의 고전과 근현대의 사상과 이론을 책으로 펴내온 도서출판 길이 이번에 독일의 대문호 요한 볼프강 폰 괴테(Johann Wolfgang von Goethe, 1749~1832)의 전집을 새로 선보인다. 문학전집에서 주로 소개되던 괴테의 글들을 인문사회과학 출판사가 모아서 내는 것은,...', '파우스트 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '날개', '이상', '소설/시/에세이', '고전문학', '12,600', '문학과지성사', 1000, TO_DATE('2005년 04월', 'YYYY"년" MM"월"'), 1000, '근대와 맞닥뜨린 식민지 조선 당대의 기념비요 자화상 역할을 하는 이상의 소설 가운데 대표 단편 11편이 수록되어 있다. ‘천재’와 ‘광인’이라는 꼬리표와 함께 전위적이고 해체적인 글쓰기로 한국의 모더니즘 문학사를 개척한 작가 이상은 자유연상, 자동기술, 내적 독백 등의 실험적 구성과 문체로 식민지 근대와 그...', '날개.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이방인', '알베르 까뮈 유기환', '소설/시/에세이', '고전문학', '6,930', '현대지성', 1000, TO_DATE('2023년 02월', 'YYYY"년" MM"월"'), 1000, '익숙한 세계를 벗어나 진정한 자신을 찾는 독자들에게마르지 않는 성찰의 재료가 되어준 소설일러스트레이터 윤예지의 컬러 일러스트 11점 수록1942년 독일 점령하에 놓인 잿빛 파리에서 눈부신 알제리의 태양이 지배하는 소설 『이방인』이 출간되었다. 이 소설은 출간 즉시 반향을 일으켜, ‘이방인’ 같은 존재였던 카뮈...', '이방인.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이윤기의 그리스 로마 신화 (특별 합본판)', '이윤기', '소설/시/에세이', '고전문학', '35,820', '웅진지식하우스', 1000, TO_DATE('2020년 05월', 'YYYY"년" MM"월"'), 1000, '21세기 한국인의 교양 지도를 바꿔놓은 책!『이윤기의 그리스 로마 신화』 출간 20주년,작가 이윤기 타계 10주기 특별 합본판 출간대한민국에 그리스 로마 신화 열풍을 불러일으킨 주역이자 200만 이상의 독자의 사랑을 받아온 ‘국민 신화 책’인 『이윤기의 그리스 로마 신화』 첫 출간 20주년을 기념하고 이윤기 선...', '이윤기의 그리스 로마 신화 (특별 합본판).jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '단테의 신곡', '단테 양억관', '소설/시/에세이', '고전문학', '10,800', '황금부엉이', 1000, TO_DATE('2016년 01월', 'YYYY"년" MM"월"'), 1000, '다니구치 에리야의 현대적 해석으로 재탄생한 [신곡]을 고흐가 ‘최고의 민중화가’라는 칭찬을 아끼지 않았던 19세기 천재 화가 구스타브 도레의 생생한 삽화로 즐기자!호메로스와 베르길리우스가 쌓은 장편서사시의 전통을 잇는 불멸의 고전으로 평가받는 [신곡]은 단테의 인간적 고뇌와 슬픔, 사랑, 희망 등이 작품 전반...', '단테의 신곡.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '걸리버여행기', '조나단 스위프트 이종인', '소설/시/에세이', '고전문학', '10,350', '현대지성', 1000, TO_DATE('2019년 09월', 'YYYY"년" MM"월"'), 1000, '『동물농장』 조지 오웰이 극찬한 최고의 풍자문학 완역본환상적인 모험에 숨겨진 인간과 사회에 대한 신랄한 풍자 풍자문학의 대가 조너선 스위프트의 『걸리버 여행기』는 걸리버의 환상적인 모험담을 통해 당대의 정치사회와 인간 문명을 통렬하게 비판한다. 스위프트는 이 작품의 의도는 세상 사람들을 즐겁게 해주려...', '걸리버여행기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이반 데니소비치 수용소의 하루', '알렉산드르 이자에비치 솔제니친', '소설/시/에세이', '고전문학', '7,200', '민음사', 1000, TO_DATE('2000년 04월', 'YYYY"년" MM"월"'), 1000, '한 개인의 비극적 운명을 통해 지배권력의 허상을 폭로한 소설노벨 문학상 수상 작가 솔제니친의 대표작!작가 솔제니친이 직접 경험했던 노동수용소 생활의 하루 일상을 세련되고 절제된 필치로 묘사한 작품이다. 이 작품은 평범하고 가련한 이반 데니소비치라는 인물을 통해 지배권력에 의해 죄없이 고통당하는 힘없는 약...', '이반 데니소비치 수용소의 하루.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '1984', '조지 오웰 한기찬', '소설/시/에세이', '고전문학', '13,320', '소담출판사', 1000, TO_DATE('2021년 12월', 'YYYY"년" MM"월"'), 1000, '인간의 기본 욕구를 억제하는 독재 권력 사회에서부조리함에 항거하는 개인의 최후를 예리하고 강력하게 묘사한 작품『1984』는 미래 예언적 디스토피아 소설이다. 조지 오웰은 『1984』에서 독재 체제의 현실을 여실히 보여 준다. 감정을 통제하고, 사고의 범위를 말살함으로써 종국에는 인간의 모든 가치를 제거하려는 독...', '1984.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '동물농장', '조지 오웰', '소설/시/에세이', '고전문학', '7,920', '열린책들', 1000, TO_DATE('2009년 11월', 'YYYY"년" MM"월"'), 1000, '인간은 평등하다, 그러나 어떤 인간은 특별히 더 평등하다!당시 영국의 동맹이던 스탈린을 가차 없이 공격하는 내용 때문에 출판사로부터 출간을 거부당하여 1943년에 써놓고도 출간하지 못할 뻔 하였던 조지 오웰의 소설이다. 동물들이 존스 씨를 몰아내고 스스로 농장을 경영해 나간다는 간명하고도 비극적인 우화는 세계...', '동물농장.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '유토피아', '토머스 모어 박문재', '소설/시/에세이', '고전문학', '10,350', '현대지성', 1000, TO_DATE('2020년 11월', 'YYYY"년" MM"월"'), 1000, '기본소득, 공공주택, 6시간 노동, 경제적 평등, 공유사회… 현재 논의되는 이상국가의 기본 틀을 이미 500년 전에 제시하다독실한 가톨릭교도인 토머스 모어의 신념과 사상이 녹아들어 있으면서도, 르네상스 인문주의자로서의 파격적 면모를 가감 없이 보여주는 수작, 『유토피아』가 현대지성 클래식 33번으로 출간되었다....', '유토피아.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '탈무드', '유대인 랍비 김이랑 최경락', '소설/시/에세이', '고전문학', '9,720', '시간과공간사', 1000, TO_DATE('2021년 12월', 'YYYY"년" MM"월"'), 1000, '빌 게이츠, 스티븐 스필버그, 마크 저커버그 등세계적으로 성공한 유대인들이 늘 이야기하는 필독고전!유대인의 오천 년 지혜를 녹여낸 『탈무드』를 컬러 일러스트로 읽는다!『탈무드』는 모세 5경(흔히 ‘토라’라고 함) 가운데 민간으로 전승되는 율법을 담은 ‘미쉬나’와 이에 대한 유대 랍비들의 해설판인 ‘게마라’...', '탈무드.jpg');
