INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '삼국지 원전 완역판 세트', '나관중 요시카와 에이지 바른번역', '소설/시/에세이', '시/희곡', '19,800', '코너스톤(도서)', 1000, TO_DATE('2020년 03월', 'YYYY"년" MM"월"'), 1000, '천하를 얻기 위해 위·촉·오 삼국의 영웅들이 움직인다!시대를 넘어 전해지는 영웅들의 힘과 지혜가 담긴 명작 『삼국지』를 요시카와 에이지판 원전 최신 완역판으로 만나보자.세 번 이상 읽지 않으면 인생을 논하지 말라는 바로 그 책, 아시아에서 가장 유명하고 누구나 다 아는 나관중의 [삼국지통속연의]가 일본 대중문...', '삼국지 원전 완역판 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '호밀밭의 파수꾼', '제롬 데이비드 샐린저 정영목', '소설/시/에세이', '시/희곡', '9,000', '민음사', 1000, TO_DATE('2023년 01월', 'YYYY"년" MM"월"'), 1000, '2023년 새로 출간하는 『호밀밭의 파수꾼』은 옮긴이 정영목 교수가 주인공 홀든 콜필드의 개성을 한층 더 생생히 표현하기 위해 원작의 문체와 문형에 가장 가까운 한국어 문장을 고심하며 저작권자의 자문과 검수를 거쳐 완성한 텍스트이다. 뿐만 아니라, 2020년대 한국 독자들의 생생한 문화적 문학적 감수성에도 부합하...', '호밀밭의 파수꾼.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '신곡 세트', '단테 박상진', '소설/시/에세이', '시/희곡', '26,100', '민음사', 1000, TO_DATE('2013년 08월', 'YYYY"년" MM"월"'), 1000, '중세의 암흑을 깨고 근대의 여명을 밝힌 지식인 단테인간사의 모든 주제를 실천적으로 고민한 깊이 있는 성찰시성(詩聖) 단테의 웅장한 서사시 『신곡』은 그가 정치적 활동으로 인해 고향 피렌체에서 추방당한 뒤 세상을 떠나기까지 20여 년에 걸친 유랑 중에 써 낸 작품이다. 현실에 대한 비판서인 동시에, 중세의 모든 ...', '신곡 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오만과 편견', '제인 오스틴', '소설/시/에세이', '시/희곡', '11,700', '민음사', 1000, TO_DATE('2003년 09월', 'YYYY"년" MM"월"'), 1000, '영국 BBC의 지난 천년간 최고의 문학가 조사에서 셰익스피어에 이어 2위를 차지할 만큼 영국인의 큰 사랑을 받고 있는 작가, 제인 오스틴. 이번에 출간된 『오만과 편견』은 두 영문학자들이 10년 동안 꼼꼼한 원문 대조 과정과 교차 검토 과정을 거쳐 탄생한 번역본이다. 오역이나 부정확한 번역을 없앴음은 물론, 오스...', '오만과 편견.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '빨강 머리 앤', '루시 모드 몽고메리 박혜원', '소설/시/에세이', '시/희곡', '15,120', '더모던', 1000, TO_DATE('2019년 05월', 'YYYY"년" MM"월"'), 1000, '앨리스 이래 가장 사랑스러운 아이라는 마크 트웨인의 말마따나, 역대 최강의 러블리 캐릭터 ‘앤 셜리’ 이야기의 첫 권인 『빨강 머리 앤』(원제: 초록 지붕 집의 앤 Anne of Green Gables)이다. 주근깨 빼빼 마른~ 빨강 머리~라는 주제곡이 절로 흥얼거려질 만큼 유명한 동명의 TV 애니메이션 원화를 ‘만화책’...', '빨강 머리 앤.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '모비딕', '허먼 멜빌 레이먼드 비숍 이종인', '소설/시/에세이', '시/희곡', '17,910', '현대지성', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '드라마 [이상한 변호사 우영우] 속 우영우가 읽은 소설국내 최초 ‘레이먼드 비숍’ 목판화 일러스트 수록 완역본절대적 진리만을 강요하던 폭력의 시대에 맞서실험적이고 전위적인 문학의 효시가 된 불후의 고전 『모비 딕』은 단순한 해양모험소설이 아니라 수많은 상징과 은유를 품은 다면적인 소설이다. 나를 이슈메...', '모비딕.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '사람은 무엇으로 사는가', '레프 톨스토이 홍대화', '소설/시/에세이', '시/희곡', '6,930', '현대지성', 1000, TO_DATE('2021년 02월', 'YYYY"년" MM"월"'), 1000, '자살 직전까지 갔던 톨스토이의 삶을 통째로 바꾼 삶의 진리… 그 깨달음을 오롯이 녹여낸 명품 단편 모음!위대한 소설가로 세계적인 명성을 누렸던 시절에도 채울 수 없었던 톨스토이의 마음을 만족하게 한 것은 무엇이었을까? 그 깨달음으로 살아갈 이유를 찾은 후 작품의 색깔까지 완전히 바꾸어 집필한 10편의 명 단편...', '사람은 무엇으로 사는가.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '모비 딕', '허먼 멜빌 김석희', '소설/시/에세이', '시/희곡', '19,800', '작가정신', 1000, TO_DATE('2011년 05월', 'YYYY"년" MM"월"'), 1000, '서두에서부터 ‘고래’에 대한 ‘어원’ 탐구와 문헌 ‘발췌록’이 등장하고, 작가의 체험과 도서관에서 조사하고 연구한 고래와 포경에 대한 갖가지 지식이 총망라된 이 독특한 소설은 출간 당시에는 어렵고 낯설다는 이유로 외면당했지만 작가가 죽고 30여 년 후에 재평가되기 시작했고 오늘날 미국문학을 대표하는 걸작...', '모비 딕.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '초판본 하늘과 바람과 별과 詩 : 윤동주 유고시집', '윤동주', '소설/시/에세이', '시/희곡', '8,820', '소와다리', 1000, TO_DATE('2016년 01월', 'YYYY"년" MM"월"'), 1000, '별이 된 유고시집 [하늘과 바람과 별과 詩] 1955년 10주기 기념 증보판죽는 날까지 하늘을 우르러 한 점 부끄럼이 없기를...일본 유학 중 독립운동 및 한글창작 혐의로 체포, 조사 과정에서 이를 부인하지 않고 옥중에서 요절한 민족시인 청년 윤동주. 직접 지은 시처럼 짧지만 한 점 부끄럼 없는 삶을 살다 그는 끝내 ...', '초판본 하늘과 바람과 별과 詩 : 윤동주 유고시집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '톨스토이 단편선', '레프 톨스토이 김은경', '소설/시/에세이', '시/희곡', '8,100', '인디북', 1000, TO_DATE('2005년 12월', 'YYYY"년" MM"월"'), 1000, '세계적 문호 톨스토이의 단편 모음집. 톨스토이의 인생관, 철학이 담겨져 있는 대표 단편작 사람은 무엇으로 사는가 외 바보 이반의 이야기, 사람에겐 얼마만큼의 땅이 필요한가 등의 작품이 수록되어 있다....', '톨스토이 단편선.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '셰익스피어 4대 비극 컬렉션 세트', '윌리엄 셰익스피어', '소설/시/에세이', '시/희곡', '20,520', '민음사', 1000, TO_DATE('2012년 11월', 'YYYY"년" MM"월"'), 1000, '인간의 고통에 대한 가장 원숙하고도 냉혹한 성찰, 민음사의 세계문학전집 셰익스피어 4대 비극을 세트로 묶었다. 존재의 비극을 탐색한 극문학의 정수 『햄릿』, 모든 시대를 막론하고 가장 현대적인 작품 『오셀로』, 셰익스피어 4대 비극 중 가장 화려하고 잔인한 작품 『맥베스』, 소포클레스의 『안티고네』와 비견되...', '셰익스피어 4대 비극 컬렉션 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 1', '요한 볼프강 폰 괴테', '소설/시/에세이', '시/희곡', '8,550', '민음사', 1000, TO_DATE('1999년 03월', 'YYYY"년" MM"월"'), 1000, '괴테 탄생 250주년을 맞이하여 민음사에서 펴낸 괴테 특별선 중 파우스트 부분. 전 2권으로 구성되어 있다. 자연 연구가, 미술가였으며, 자신의 공국에 봉사한 정치 지도자이기도 했던 괴테. 후세에는 자유에의 의무, 낯설고 이질적인 것에 대한 긍정심, 존재하는 것에 대한 감사함. 그리고 장래의 것에 대한 비판적 개방성...', '파우스트 1.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '안나 카레니나 세트', '레프 톨스토이', '소설/시/에세이', '시/희곡', '27,720', '민음사', 1000, TO_DATE('2012년 11월', 'YYYY"년" MM"월"'), 1000, '19세기 러시아가 낳은 위대한 예술가 톨스토이.그가 남긴 최고의 리얼리즘 소설19세기 러시아의 대문호 레프 톨스토이의 『안나 카레니나』(Анна Каренина)가 민음사 세계문학전집 세트로 출간되었다. 동시대 작가인 도스토예프스키로부터 완벽한 예술 작품이라는 평가와, 역시 러시아 출신 소설가인 나보...', '안나 카레니나 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 2', '요한 볼프강 폰 괴테', '소설/시/에세이', '시/희곡', '9,900', '민음사', 1000, TO_DATE('1999년 03월', 'YYYY"년" MM"월"'), 1000, '괴테 탄생 250주년을 맞이하여 민음사에서 펴낸 괴테 특별선 중 파우스트 부분. 전 2권으로 구성되어 있다. 자연 연구가, 미술가였으며, 자신의 공국에 봉사한 정치 지도자이기도 했던 괴테. 후세에는 자유에의 의무, 낯설고 이질적인 것에 대한 긍정심, 존재하는 것에 대한 감사함. 그리고 장래의 것에 대한 비판적 개방성...', '파우스트 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '햄릿', '윌리엄 셰익스피어 최종철', '소설/시/에세이', '시/희곡', '6,300', '민음사', 1000, TO_DATE('2001년 02월', 'YYYY"년" MM"월"'), 1000, '셰익스피어 4대 비극 중 하나. 예민한 감수성과 지성, 섬세하고 결백한 성격의 소유자 햄릿은 어느 날 존경하던 아버지를 잃고, 어머니까지 숙부와 재혼함으로써 큰 충격을 받고 인생 자체에 회의를 느끼게 된다. 그러던 중 아버지의 유령이 나타나 자신이 동생에게 살해되었다는 사실을 밝히자 햄릿은 복수의 일념에 사로...', '햄릿.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '신곡', '단테 김운찬', '소설/시/에세이', '시/희곡', '32,400', '열린책들', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '귀스타브 도레의 삽화를 모두 수록한김운찬 교수의 이탈리아어 완역본귀스타브 도레의 아름다운 삽화를 수록한 단테 알리기에리의 『신곡』 개정판. 이번 개정판은 대구가톨릭대학교 프란치스코칼리지의 김운찬 교수가 번역하여 2007년 출간한 이탈리아어 완역본 『신곡』을 번역과 편집, 디자인을 모두 새롭게 손보아 제작...', '신곡.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파우스트 세트', '요한 볼프강 폰 괴테 전영애', '소설/시/에세이', '시/희곡', '54,000', '길', 1000, TO_DATE('2019년 06월', 'YYYY"년" MM"월"'), 1000, '세상을 해석하고 바꾸는 데 바탕이 되는 인문사회과학 분야의 고전과 근현대의 사상과 이론을 책으로 펴내온 도서출판 길이 이번에 독일의 대문호 요한 볼프강 폰 괴테(Johann Wolfgang von Goethe, 1749~1832)의 전집을 새로 선보인다. 문학전집에서 주로 소개되던 괴테의 글들을 인문사회과학 출판사가 모아서 내는 것은,...', '파우스트 세트.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '날개', '이상', '소설/시/에세이', '시/희곡', '12,600', '문학과지성사', 1000, TO_DATE('2005년 04월', 'YYYY"년" MM"월"'), 1000, '근대와 맞닥뜨린 식민지 조선 당대의 기념비요 자화상 역할을 하는 이상의 소설 가운데 대표 단편 11편이 수록되어 있다. ‘천재’와 ‘광인’이라는 꼬리표와 함께 전위적이고 해체적인 글쓰기로 한국의 모더니즘 문학사를 개척한 작가 이상은 자유연상, 자동기술, 내적 독백 등의 실험적 구성과 문체로 식민지 근대와 그...', '날개.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이방인', '알베르 까뮈 유기환', '소설/시/에세이', '시/희곡', '6,930', '현대지성', 1000, TO_DATE('2023년 02월', 'YYYY"년" MM"월"'), 1000, '익숙한 세계를 벗어나 진정한 자신을 찾는 독자들에게마르지 않는 성찰의 재료가 되어준 소설일러스트레이터 윤예지의 컬러 일러스트 11점 수록1942년 독일 점령하에 놓인 잿빛 파리에서 눈부신 알제리의 태양이 지배하는 소설 『이방인』이 출간되었다. 이 소설은 출간 즉시 반향을 일으켜, ‘이방인’ 같은 존재였던 카뮈...', '이방인.jpg');
