INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '단테의 신곡', '단테 양억관', '소설/시/에세이', '시/희곡', '10,800', '황금부엉이', 1000, TO_DATE('2016년 01월', 'YYYY"년" MM"월"'), 1000, '다니구치 에리야의 현대적 해석으로 재탄생한 [신곡]을 고흐가 ‘최고의 민중화가’라는 칭찬을 아끼지 않았던 19세기 천재 화가 구스타브 도레의 생생한 삽화로 즐기자!호메로스와 베르길리우스가 쌓은 장편서사시의 전통을 잇는 불멸의 고전으로 평가받는 [신곡]은 단테의 인간적 고뇌와 슬픔, 사랑, 희망 등이 작품 전반...', '단테의 신곡.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '걸리버여행기', '조나단 스위프트 이종인', '소설/시/에세이', '시/희곡', '10,350', '현대지성', 1000, TO_DATE('2019년 09월', 'YYYY"년" MM"월"'), 1000, '『동물농장』 조지 오웰이 극찬한 최고의 풍자문학 완역본환상적인 모험에 숨겨진 인간과 사회에 대한 신랄한 풍자 풍자문학의 대가 조너선 스위프트의 『걸리버 여행기』는 걸리버의 환상적인 모험담을 통해 당대의 정치사회와 인간 문명을 통렬하게 비판한다. 스위프트는 이 작품의 의도는 세상 사람들을 즐겁게 해주려...', '걸리버여행기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이반 데니소비치 수용소의 하루', '알렉산드르 이자에비치 솔제니친', '소설/시/에세이', '시/희곡', '7,200', '민음사', 1000, TO_DATE('2000년 04월', 'YYYY"년" MM"월"'), 1000, '한 개인의 비극적 운명을 통해 지배권력의 허상을 폭로한 소설노벨 문학상 수상 작가 솔제니친의 대표작!작가 솔제니친이 직접 경험했던 노동수용소 생활의 하루 일상을 세련되고 절제된 필치로 묘사한 작품이다. 이 작품은 평범하고 가련한 이반 데니소비치라는 인물을 통해 지배권력에 의해 죄없이 고통당하는 힘없는 약...', '이반 데니소비치 수용소의 하루.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '1984', '조지 오웰 한기찬', '소설/시/에세이', '시/희곡', '13,320', '소담출판사', 1000, TO_DATE('2021년 12월', 'YYYY"년" MM"월"'), 1000, '인간의 기본 욕구를 억제하는 독재 권력 사회에서부조리함에 항거하는 개인의 최후를 예리하고 강력하게 묘사한 작품『1984』는 미래 예언적 디스토피아 소설이다. 조지 오웰은 『1984』에서 독재 체제의 현실을 여실히 보여 준다. 감정을 통제하고, 사고의 범위를 말살함으로써 종국에는 인간의 모든 가치를 제거하려는 독...', '1984.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '동물농장', '조지 오웰', '소설/시/에세이', '시/희곡', '7,920', '열린책들', 1000, TO_DATE('2009년 11월', 'YYYY"년" MM"월"'), 1000, '인간은 평등하다, 그러나 어떤 인간은 특별히 더 평등하다!당시 영국의 동맹이던 스탈린을 가차 없이 공격하는 내용 때문에 출판사로부터 출간을 거부당하여 1943년에 써놓고도 출간하지 못할 뻔 하였던 조지 오웰의 소설이다. 동물들이 존스 씨를 몰아내고 스스로 농장을 경영해 나간다는 간명하고도 비극적인 우화는 세계...', '동물농장.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '유토피아', '토머스 모어 박문재', '소설/시/에세이', '시/희곡', '10,350', '현대지성', 1000, TO_DATE('2020년 11월', 'YYYY"년" MM"월"'), 1000, '기본소득, 공공주택, 6시간 노동, 경제적 평등, 공유사회… 현재 논의되는 이상국가의 기본 틀을 이미 500년 전에 제시하다독실한 가톨릭교도인 토머스 모어의 신념과 사상이 녹아들어 있으면서도, 르네상스 인문주의자로서의 파격적 면모를 가감 없이 보여주는 수작, 『유토피아』가 현대지성 클래식 33번으로 출간되었다....', '유토피아.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '탈무드', '유대인 랍비 김이랑 최경락', '소설/시/에세이', '시/희곡', '9,720', '시간과공간사', 1000, TO_DATE('2021년 12월', 'YYYY"년" MM"월"'), 1000, '빌 게이츠, 스티븐 스필버그, 마크 저커버그 등세계적으로 성공한 유대인들이 늘 이야기하는 필독고전!유대인의 오천 년 지혜를 녹여낸 『탈무드』를 컬러 일러스트로 읽는다!『탈무드』는 모세 5경(흔히 ‘토라’라고 함) 가운데 민간으로 전승되는 율법을 담은 ‘미쉬나’와 이에 대한 유대 랍비들의 해설판인 ‘게마라’...', '탈무드.jpg');
