INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '자전거여행 2', '김훈', '에세이', '여행 에세이', '13,500', '문학동네', 1000, TO_DATE('2014년 10월', 'YYYY"년" MM"월"'), 1000, '줄을 치고 또 쳐도 마음을 흔드는 새로운 문장들이 넘쳐나는 게 김훈의 책입니다._박웅현, 『책은 도끼다』몸과 마음과 풍경이 만나고 갈라서는 언저리에서 태어나는 김훈 산문의 향연!김훈 산문의 정수(精髓)라 할 산문 『자전거여행』이 재출간된다. 언젠가 그는 나는 사실만을 가지런하게 챙기는 문장이 마음에 듭...', '자전거여행 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나를 부르는 숲', '빌 브라이슨 홍은택', '에세이', '여행 에세이', '12,150', '까치(까치글방)', 1000, TO_DATE('2018년 01월', 'YYYY"년" MM"월"'), 1000, '자연에 대한 인간의 사랑과 도전을 그린 빌 브라이슨의 걸작숨막히는 대장관과 유쾌한 이야기들이 가득한 세계 최장의 트레일 종주기세계에서 가장 유러머스한 여행작가 빌 브라이슨의 진면목을 제대로 느낄 수 있는 그의 대표작인 이 책은 세계에서 가장 길며, 수많은 위험이 도사리고 있지만 아름다운 장관이 펼쳐지...', '나를 부르는 숲.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '먼 북소리', '무라카미 하루키 윤성원', '에세이', '여행 에세이', '14,400', '문학사상', 1000, TO_DATE('2019년 09월', 'YYYY"년" MM"월"'), 1000, '멀리서 들려오는 북소리에 이끌려 나는 긴 여행을 떠났다.낡은 외투를 입고 모든 것을 뒤로한 채…1997년 10월에 출간되었던 하루키의 걸작 여행 에세이가 시대의 흐름에 맞추어 양장판으로 새롭게 개장하여 발간되었다. 하루키가 1986년 가을부터 89년 가을까지 약 3년간 유럽을 여행하는 동안 문학과 인생, 소설쓰기에 대...', '먼 북소리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나를 채우는 여행의 기술', '인생학교 케이채 알랭 드 보통', '에세이', '여행 에세이', '13,500', '오렌지디', 1000, TO_DATE('2023년 02월', 'YYYY"년" MM"월"'), 1000, '당신에게 ‘여행’이란 무엇인가요?알랭 드 보통이 기획하고 ‘인생학교’가 만든,내 삶을 채우는 여행을 위한 가장 완벽한 안내서* 여행의 영감을 주는 풀컬러 사진 수록*기록을 위한 노트 페이지&특별 책갈피비행기표와 여권, 그리고 이 책이 있다면여행 준비는 모두 끝났다. _아마존 북 리뷰『사유 식탁』에 이은 인...', '나를 채우는 여행의 기술.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '일상이 고고학, 나 혼자 국립중앙박물관', '황윤', '에세이', '여행 에세이', '18,000', '책읽는고양이', 1000, TO_DATE('2022년 07월', 'YYYY"년" MM"월"'), 1000, '색다른 스토리텔링으로 만나는 국립중앙박물관그 주인공은 금동반가사유상이 책은 국립중앙박물관 ‘사유의 방’에 전시된 금동반가사유상 두 분과의 귀한 만남을 더욱 깊고 풍부하게 감상하기 위한 탐구서이자 안내서이다. 박물관 마니아이자 역사 덕후인 황윤 작가는 오래도록 국립중앙박물관을 보는 자신만의 스토리텔링...', '일상이 고고학, 나 혼자 국립중앙박물관.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '어쩌다 외교관의 뉴욕 랩소디', '은파', '에세이', '여행 에세이', '15,120', '대경북스', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '어린 왕자와 함께한 뉴욕에서의 외교관 3년 살이우연찮게 외교관이 되어 뉴욕 영사관에 부임한 필자가 우리나라 외교부와 뉴욕 영사관, 그리고 뉴욕 생활 속에서 어린 왕자의 시각으로 ‘나’를 찾아가는 과정을 담은 글이다. 필자가 좋아하는 ‘어린 왕자’의 눈으로 미국이라는 낯선 나라의 모습과 그곳에서 터를 잡고 생...', '어쩌다 외교관의 뉴욕 랩소디.jpg');
