INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '유럽 캠핑 30일', '안수지', '에세이', '여행 에세이', '16,200', 'M31', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '유럽에서 캠핑으로 한 달 살기!‘텐트 밖 유럽’은 꾸미지 않은 자연과 아웃도어의 천국! 유럽 여행, 하면 흔히 뻔한 도시와 관광지, 미술관 등을 떠올리기 쉽지만, 이 책은 사람의 손길을 최소화한 꾸밈없는 모습의 유럽을 소개한다. 텐트 밖만 나서면 숨 막히게 아름다운 자연과 여행자들의 소소한 이야기꽃이 펼쳐지는 ...', '유럽 캠핑 30일.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '기차와 생맥주', '최민석', '에세이', '여행 에세이', '13,500', '북스톤', 1000, TO_DATE('2022년 07월', 'YYYY"년" MM"월"'), 1000, '일 같은 여행, 여행 같은 일을 하며 먹고사는 소설가 최민석, 여행 잡지를 창간하다2010년 ‘창비 신인소설상’을 받으며 데뷔, 2012년 ‘오늘의 작가상’을 수상한 소설가 최민석이 여행 잡지를 창간했다. 제호는 『기차와 생맥주』.대문호들은 하나같이 날씬했다는 사실에 자극받아 글쓰기보다 다이어트를 먼저 했고, 망...', '기차와 생맥주.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '헤어진 이들은 홍콩에서 다시 만난다', '주성철', '에세이', '여행 에세이', '14,850', '김영사', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '장국영과 양조위와 장만옥이 만나고 헤어지던 홍콩을 걷다[방구석 1열] 주성철 기자의 홍콩영화 성지 순례기박찬욱, 변영주, 류승완 감독 추천![중경삼림] 양조위의 대사처럼 홍콩영화 촬영지 중 아무 곳이나 당신이 원하는 곳으로 안내해줄 홍콩 여행서 『헤어진 이들은 홍콩에서 다시 만난다』가 출간되었다. 이 환상...', '헤어진 이들은 홍콩에서 다시 만난다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '길 위의 낭만, 순례길 신혼여행을 꿈꾸다', '김리나 권영범', '에세이', '여행 에세이', '18,000', '크루', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '배낭 메고 신혼여행!영국에서부터 이탈리아 로마까지우리 무사히 완주할 수 있을까?우리가 처음 비아 프란치제나 순례길을 생각한 때는 풋풋한 연애 초기였다. 나는 ‘비아 프란치제나’라는 생소한 이름을 마음에 담았다. 시간이 흘러 우리의 결혼식이 딱 한 달 남았을 때 나는 비아 프란치제나 순례길 이야기를 다시 꺼냈...', '길 위의 낭만, 순례길 신혼여행을 꿈꾸다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '우리는 우리를 잊지 못하고 (김민철×키미앤일이 썸머 에디션)', '김민철', '에세이', '여행 에세이', '15,300', '미디어창비', 1000, TO_DATE('2021년 04월', 'YYYY"년" MM"월"'), 1000, '모두가 간절히 풀려나길 바랐던, 그 단어가 풀려났어요. 바로 ‘여행’입니다.잃어보고 나서야 그 의미를 보다 선명하게 알게 된 여행의 감각을 전하는 김민철과다시 떠나게 될 새로운 목적지를 기대하게 만드는 키미앤일이의 콜라보 에디션!마음껏 떠날 수 없는 여행에 대한 그리움을 달래준 김민철의 『우리는 우리를...', '우리는 우리를 잊지 못하고 (김민철×키미앤일이 썸머 에디션).jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '하늘 호수로 떠난 여행', '류시화', '에세이', '여행 에세이', '12,600', '열림원', 1000, TO_DATE('2015년 09월', 'YYYY"년" MM"월"'), 1000, '입소문으로 50만 부 판매된, 18년간의 스테디셀러 [하늘 호수로 떠난 여행] 개정판. 많은 문제를 안고 있는 나라이지만 수많은 붓다를 탄생시키고 삼장법사에서 히피에 이르기까지 새로운 길을 추구하는 다양한 사람을 끌어당긴 영적인 자기장, 궁극의 여행을 권유하는 나라가 인도이다. 삶의 근원적인 의문에 사로잡힐 때 ...', '하늘 호수로 떠난 여행.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '버번 위스키의 모든 것', '조승원', '에세이', '여행 에세이', '30,400', '싱긋', 1000, TO_DATE('2020년 05월', 'YYYY"년" MM"월"'), 1000, '술꾼의 술, 버번 위스키의 세계에 들어온 것을 환영합니다이 책은 술과 음악에 심취하여 국내외 할 것 없이 관련 정보와 도서들을 찾아 헤매다가 ‘세상에 없다면 내가 쓰고 내가 첫 독자가 되겠다’는 다짐으로 집필에 착수해서 끝내 책을 내는 술꾼 조승원 기자의 세번째 책이다. 국내외의 방대한 자료를 뒤져 열심히 공...', '버번 위스키의 모든 것.jpg');
