INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아직 끝이 아니다 (어나더커버 에디션)', '김연경', '에세이', '휴먼 에세이', '13,320', '가연', 1000, TO_DATE('2021년 03월', 'YYYY"년" MM"월"'), 1000, '꿈을 꾸는 것이 무엇이든 해야 하고, 할 수 있다한국 여자배구 사상 최고의 왼쪽 공격수 김연경이 주목받지 못했던 유년시절을 이겨내고 일본과 터키에 진출하여 세계를 사로잡은 이야기를 『아직 끝나지 않았다』에 담아냈다. 모두가 인정하는 세계 정상급 배구 선수 김연경은 최적의 신체 조건과 더불어 공격과 수비를 아...', '아직 끝이 아니다 (어나더커버 에디션).jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'HEAL THE WORLD 힐 더 월드', '국제아동돕기연합', '에세이', '휴먼 에세이', '11,700', '문학동네', 1000, TO_DATE('2008년 12월', 'YYYY"년" MM"월"'), 1000, '사막 같은 세상의 한 그루 나무 같은 책국제아동돕기연합에서 만드는 월간 『Ue』의 콘텐츠를 묶은 것으로, 우리의 무관심을 따끔하게 찌르는 진실과, 세상을 바꾸는 데 필요한 따뜻한 지식과, 일상 속에서 쉽게 행할 수 있는 실천과, 작은 힘을 모아 거대한 변화를 꿈꾸는 사람들의 뭉클한 감동을 담았다.', 'HEAL THE WORLD 힐 더 월드.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나는 사이보그가 되기로 했다', '피터 스콧-모건 김명주', '에세이', '휴먼 에세이', '19,800', '김영사', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '불치병에 맞서 사이보그로 진화한 어느 로봇공학자의 특별한 도전자기 몸을 AI와 융합한 최초의 인간 ‘피터 2.0’이 직접 써내려간 감동 실화인간이란 무엇인가? 과학은 무엇을 해낼 수 있는가? 한 편의 SF 영화보다 더 영화 같은 이야기다. 이는 놀랍게도 실화이며, 먼 미래의 일이 아닌 오늘날 우리가 마주한 현실이다. ...', '나는 사이보그가 되기로 했다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '시골의사의 아름다운 동행 1', '박경철', '에세이', '휴먼 에세이', '11,700', '리더스북', 1000, TO_DATE('2011년 10월', 'YYYY"년" MM"월"'), 1000, '100만 독자를 울리고 웃긴 『시골의사의 아름다운 동행』표지와 본문편집을 정비한 개정판으로 다시 만나다시골 외과의사가 병원이라는 풍경 속에서 깊고 따뜻한 시선으로 건져 올린 62편의 에피소드를 1, 2권에 걸쳐 엮은 에세이.', '시골의사의 아름다운 동행 1.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '백석 평전', '안도현', '에세이', '휴먼 에세이', '16,200', '다산책방', 1000, TO_DATE('2014년 06월', 'YYYY"년" MM"월"'), 1000, '30년간 백석을 짝사랑해온 시인 안도현이 완성한우리시대 최고의 평전! 백석의 첫 시집 『사슴』은 1936년 1월 20일, 100부 한정판으로 출간되어 세상에 선을 보였다. 백석은 한 권의 시집을 실로 한 개의 포탄을 던지는 것처럼 새해 첫머리에 시단에 내던졌다.(김기림) 백석의 시집을 구하지 못한 윤동주는 도서관에...', '백석 평전.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오늘도 택하겠습니다', '박용택', '에세이', '휴먼 에세이', '15,120', '글의온도', 1000, TO_DATE('2021년 09월', 'YYYY"년" MM"월"'), 1000, 'LG트윈스 33번 야구선수에서 [노는 브로] 맏형으로 나타나 이보다 솔직할 수 없는 박용택의 야구와 인생 첫 에세이 LG 트윈스 박용택의 등번호 33번은 이제 영구결번을 앞두고 있다. 그와 함께 KBO 최초 200홈런 300도루, 10년 연속 3할, 2236경기 2504안타 등 선수로서 남긴 기록은 한국 야구계의 전설이 되었다. 그런 그...', '오늘도 택하겠습니다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '너무 늦기 전에 들어야 할 죽음학 강의', '최준식 김호연', '에세이', '휴먼 에세이', '10,800', '김영사', 1000, TO_DATE('2014년 07월', 'YYYY"년" MM"월"'), 1000, '세계적인 한국학자이자 죽음학의 권위자 최준식 교수가 안내하는 삶과 죽음에 대한 명상 수업늦기 전에 죽음을 배우십시오, 그래야만 삶을 알게 될 것입니다인생에서 가장 중요한 순간을 맞이할 준비가 되었는가? 우리가 낯선 곳을 여행하기 전에 공부를 하듯, 죽음이라는 새로운 세계를 여행하기 위해서는 미리미리 준...', '너무 늦기 전에 들어야 할 죽음학 강의.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '눈 감으면 보이는 것들', '신순규', '에세이', '휴먼 에세이', '12,420', '판미동', 1000, TO_DATE('2015년 10월', 'YYYY"년" MM"월"'), 1000, '꿈, 일, 가족, 본다는 것…누군가에겐 평범한 일이지만나에겐 기적 같은 일월가 시각장애인 애널리스트 신순규가 전하는우리가 살면서 잊지 말아야 할 소중한 것들시각장애를 넘어 하버드에서 월스트리트까지스펙보다 더 소중한 삶의 가치를 나누다미 월가의 시각장애인 애널리스트 신순규가 바쁜 현대인들에게 일상적...', '눈 감으면 보이는 것들.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '달팽이가 느려도 늦지 않다', '정목', '에세이', '휴먼 에세이', '13,500', '수오서재', 1000, TO_DATE('2020년 02월', 'YYYY"년" MM"월"'), 1000, '진정 가치 있는 것을 놓치지 않도록 마음의 속도를 조절해주는 책! 따뜻한 시선으로 작은 존재에게도 곁을 내어주는 정목 스님. 오랜 시간 많은 이들의 지친 마음을 넉넉히 품어주었던 『달팽이가 느려도 늦지 않다』가 전면 개정되어 새롭게 출간되었다. 스님의 소박하고 정갈한 문장은 시간이 흘러도 여전히 우리에게 용...', '달팽이가 느려도 늦지 않다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '김성근이다', '김성근', '에세이', '휴먼 에세이', '10,800', '다산라이프', 1000, TO_DATE('2011년 12월', 'YYYY"년" MM"월"'), 1000, '감독으로 말할 수 없었던 김성근의 못다한 인생 이야기지난 8월 SK 와이번스를 그만두면서 팬들의 곁을 떠났던 야신 김성근. 감독으로는 할 수 없었던 그동안의 못다한 인생 이야기를 책에 담았다. 평생 야구밖에 모르고 살았던 그에게 야구는 곧 인생 그 자체다. 인생의 전부다. 그래서 그는 야구를 통해 인생을 배웠다. ...', '김성근이다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '마음이 얹힌 거야', '황영준', '에세이', '휴먼 에세이', '15,120', '위시라이프', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '먹방만 보여주는 시대에 소화를 권하는 책여느 직장인처럼 일상이 선사하는 피로를 먹으며 풀던 저자. 담도암에 걸려 간과 쓸개를 빼앗기고 나서야 무언가 단단히 얹혔다는 것을 깨닫는다. 드라마나 영화를 보고, 책을 읽고, 저녁이 되면 산책을 하는 암환자의 일상. 그 기록을 모아보니 소화에 대한 이야기가 되었다.', '마음이 얹힌 거야.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '주기율표', '프리모 레비 이현경', '에세이', '휴먼 에세이', '12,600', '돌베개', 1000, TO_DATE('2007년 01월', 'YYYY"년" MM"월"'), 1000, '아우슈비츠를 통해 인간성의 한계를 성찰한 현대문학의 고전, 프리모 레비의 대표작 『이것이 인간인가』와 『주기율표』 국내 최초 소개 ! 수소, 아연, 철, 칼륨... 화학 시간에 배운 주기율표 원소에서 유년시절의 추억과 사랑하는 사람들의 이야기를 떠올리는 사람이 얼마나 될까? 이탈리아를 대표하는 작가이자 토리노 ...', '주기율표.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '한 아이 1', '토리 헤이든 이희재', '에세이', '휴먼 에세이', '7,650', '아름드리미디어', 1000, TO_DATE('2008년 02월', 'YYYY"년" MM"월"'), 1000, '아동 교육 심리학의 영원한 고전, 15년 만에 완성된 한 특수교육 교사의 체험기! 『한 아이 1 One Child』은 1980년 발간되자마자 전 세계 모든 대륙에서 가슴 저린 베스트셀러로 자리 잡았다. 전 세계 독자들이 어머니에게 버림받아 고속도로에 버려지고 알코올과 마약 중독자인 아버지에게 학대받는 한 어린 소녀, 그 끔...', '한 아이 1.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '어느 소방관의 기도', '오영환', '에세이', '휴먼 에세이', '12,600', '쌤앤파커스', 1000, TO_DATE('2015년 12월', 'YYYY"년" MM"월"'), 1000, '국민이 가장 신뢰하지만, 처우는 최하위 약자인 직업, 대한민국 소방관. 소방관이 다치거나 순직할 때 국가의 작은 영웅이라고 조명하는 것은 잠시뿐, 사람들도 세상도 그들을 너무 빨리 잊는다. 하지만 소방관들은 숨도 제대로 못 쉬는 화염 속으로, 언제 무너질지 모르는 건물 속으로 오늘도 또다시 뛰어 들어간다. 《어...', '어느 소방관의 기도.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '우리는 모두 다른 세계에 산다', '조제프 쇼바네크 이정은', '에세이', '휴먼 에세이', '14,850', '현대지성', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '한 자폐인이 촘촘히 기록한, 자폐인들이 세상을 바라보는 흥미로운 관점자신이 경험한 자폐 스펙트럼을 놀랍도록 유머러스하고 담담하게 풀어냈다.- 조우성 변호사_ 드라마 [이상한 변호사 우영우]에 일부 에피소드 제공삶이 반복적으로 무너져 내릴 때, 인생을 긍정하는 지혜를 그에게서 배웠다.- 리단 작가_ 『정...', '우리는 모두 다른 세계에 산다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '라파(RAFA)', '라파엘 나달 존 칼린 천수현', '에세이', '휴먼 에세이', '18,000', '쇼비(쇼비픽쳐스)', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '그랜드슬램 22회 우승 / 프랑스오픈 14회 우승흙신이라 불리는 G.O.A.T (Greatest Of All Time)‘라파엘 나달’그가 이야기하는 성공 비하인드 스토리라파엘 나달의 공식 자서전인 이 책은 라파엘 나달과 저널리스트 존 칼린이 공동 집필하여 2011년에 스페인어로 처음 출간되었다. 그랜드슬램 우승과 테니스 경기에 대한 ...', '라파(RAFA).jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '위로', '이철환', '에세이', '휴먼 에세이', '10,800', '자음과모음(이룸)', 1000, TO_DATE('2011년 10월', 'YYYY"년" MM"월"'), 1000, '『연탄길』, 『곰보빵』, 『행복한 고물상』의 작가 이철환이 내놓은 그림 에세이다. 작가가 직접 모든 삽화를 그렸으며 ‘피터’라는 나비를 주인공으로 내세운 어른들을 위한 짧은 동화다. 주인공 피터를 통해서 전해지는 이야기들은 작가가 직접 그린 200여 점의 삽화와 함께 어우러져 큰 울림을 준다. 원고 기획부터 그...', '위로.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '박지성 마이 스토리', '박지성', '에세이', '휴먼 에세이', '19,800', '한스미디어', 1000, TO_DATE('2015년 02월', 'YYYY"년" MM"월"'), 1000, '박지성, 23년 축구 인생의 모든 것!23년간의 선수 생활을 총정리한 책시즌별 주요 기록과 베스트 경기 리뷰 수록대한민국을 대표하는 축구 선수 박지성이 은퇴 후 23년의 축구 선수 생활을 총정리한 책을 펴냈다. 이 책에는 박지성이 지난 선수 시절을 되돌아보며 떠오른 여러 기억들을 정리한 에세이와 그가 프로 선수와 ...', '박지성 마이 스토리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이건희 담대한 명언', '김옥림', '에세이', '휴먼 에세이', '14,220', '미래의서재', 1000, TO_DATE('2020년 12월', 'YYYY"년" MM"월"'), 1000, '‘위기의 승부사’ ‘불세출의 경영인’ 이건희가 남긴 어록들!오늘날의 삼성을 말할 때, 이건희 회장을 떼어놓고 말할 수는 없다. 한 사람이 한 기업의 역사가 되었고, 신화가 되었다. 이건희 회장의 진가는 삼성의 역사를 써내려갔다는 데 그치지 않고, 지금도 그리고 앞으로도 써내려갈 수 있는 역사의 토대를 구축했다...', '이건희 담대한 명언.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '내가 본 것을 당신도 볼 수 있다면', '정우성', '에세이', '휴먼 에세이', '12,150', '원더박스', 1000, TO_DATE('2019년 06월', 'YYYY"년" MM"월"'), 1000, '정우성이 만난 난민 이야기. 유엔난민기구 친선대사로 활동 중인 배우 정우성은 2014년부터 매해 한 차례 이상 해외 난민촌을 찾아 난민을 직접 만나 그들의 소식을 우리 사회에 전해 왔다. 그가 그동안 난민 보호 활동을 하며 만난 이들의 이야기와 난민 문제에 대한 생각을 책으로 엮었다. 누구라도 난민촌에서 난민들...', '내가 본 것을 당신도 볼 수 있다면.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '마이클 조던 MICHAEL JORDAN', '롤랜드 레이즌비 서종기', '에세이', '휴먼 에세이', '20,700', '1984(일구팔사)', 1000, TO_DATE('2020년 08월', 'YYYY"년" MM"월"'), 1000, '마이클 조던은 스포츠 역사에 길이 남을 찬란한 순간들을 남겼고 각 장면에는 상징적인 이름이 붙었다. 흔히 사람들은 그를 이야기할 때 절체절명의 위기마다 그가 던졌던 슛을 떠올린다. 철썩 소리와 함께 역전을 이뤄낸 슛.하지만 숱한 업적을 쌓은 그에게도 어두운 면은 있다. 무자비한 경쟁심과 도박에 골몰하는 성향이...', '마이클 조던 MICHAEL JORDAN.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나는 희망의 증거이고 싶다', '이후범', '에세이', '휴먼 에세이', '16,200', '애드미트', 1000, TO_DATE('2022년 05월', 'YYYY"년" MM"월"'), 1000, '이 책은 작가 이후범이 루게릭 판정을 받고 한양대병원에서 많은 루게릭 환우들을 만나면서 놀라울 정도로 밝은 그들로부터 많은 치유를 받고 그들과 환자생활을 하며 루게릭으로 아파하는 모든 환우들과 가족들이 함께 써 내려간 일기이다.', '나는 희망의 증거이고 싶다.jpg');
