INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오늘은 좀 매울지도 몰라', '강창래', '에세이', '음식 에세이', '12,420', '문학동네', 1000, TO_DATE('2018년 04월', 'YYYY"년" MM"월"'), 1000, '어떤 사람은 레시피를 읽고어떤 사람은 마음을 읽는 책부엌일 젬병이었던 인문학자가 부엌에서 홀로 서기를 한다. 병석에 있는 아내는 이제 어떤 음식도 제대로 소화시키지 못한다. 그나마 입에 대는 거라곤 남편이 마음을 다해 만든 요리뿐. 고통과 아픔 대신, 음식으로 만들어내는 짧은 기쁨의 순간을 붙잡아두기 위해 쓴...', '오늘은 좀 매울지도 몰라.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '사유 식탁', '알랭 드 보통 인생학교 이용재', '에세이', '음식 에세이', '34,650', '오렌지디', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '사랑과 불안을 말하는 일상의 철학자, 알랭 드 보통이 초대하는 다정한 사유의 만찬그만의 철학적 사유와 독특한 필치로 고단한 우리네 삶을 치유하는 특별한 레시피북연애와 철학을 접목한 독특한 글쓰기로 ‘닥터 러브’라는 별칭까지 얻은 알랭 드 보통이 뜻밖의 요리책으로 한국 독자를 찾아왔다. 2008년 그가 주축이 ...', '사유 식탁.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'BTS RECIPE BOOK', 'Cake', '에세이', '음식 에세이', '30,400', 'Cake', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '방탄소년단은 그동안 많은 콘텐츠에서 음식을 직접 만들고, 맛있게 먹는 모습을 보여 주었다. 『BTS RECIPE BOOK』은 방탄소년단이 여러 콘텐츠에서 만들고 먹었던 요리 중 15개를 엄선하여, 그 요리를 만들고 먹을 때 했던 말과 행동 그리고 여기에 담긴 의미를 자세하게 풀어냈다. 나아가 방탄소년단의 요리 취향도 곳곳...', 'BTS RECIPE BOOK.jpg');
