INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '도쿄에선 단 한 끼도 대충 먹을 수 없어', '바이구이', '에세이', '음식 에세이', '18,000', '중앙북스(books)', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '도쿄통이 알려주는진짜 도쿄 음식 이야기 85고급 코스 요리보다 특별한‘도쿄식 와쇼쿠’를 느낄 수 있는 책먹는 것에 진심인 사람들을 위한 도쿄 미식 이야기 『도쿄에선 단 한 끼도 대충 먹을 수 없어』가 출간됐다. 도쿄에서 유년기를 보낸 저자 바이구이는 도쿄 음식의 진가를 고급 요리가 아닌 가장 평범한 도쿄 사람...', '도쿄에선 단 한 끼도 대충 먹을 수 없어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '끼니들', '김수경', '에세이', '음식 에세이', '11,700', '꿈꾸는인생', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '들시리즈 여섯 번째 책맛과 향으로, 어떤 감정으로 남아 있는 끼니의 장면들‘들시리즈’는 한 사람이 책 한 권 분량을 꽉 채워 말할 수 있는 무언가에 대한 에세이이다. 즐거운 것이나 괴로운 것, 재미있는 법칙, 배워야 할 삶의 태도 등 그 어떤 것도 주제가 될 수 있다. 다양한 이야기를 여러 사람의 목소리로 듣고 싶어...', '끼니들.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '요즘 사는 맛', '김겨울 김현민 김혼비 디에디트 박서련 박정민 손현 요조 임진아 천선란 정보 더 보기/감추기 김겨울 김현민 김혼비 디에디트 박서련 박정민 손현 요조 임진아 천선란 최민석 핫펠트(예은)', '에세이', '음식 에세이', '14,400', '위즈덤하우스', 1000, TO_DATE('2022년 02월', 'YYYY"년" MM"월"'), 1000, '오늘도 내일도 맛있게 먹는다.달콤하고 상큼하고 고소한 인생을 위해!별것 아닌 것이 별것이 되는 먹는 기쁨, 함께하는 설렘, 나누는 즐거움인간 생활의 세 가지 기본 요소인 의식주 가운데 가장 손쉽게 다른 사람과 함께할 수 있는 것은 아무래도 ‘먹는 일’ 아닐까? 누군가와 밥을 먹고 차를 마시고 술 한잔 기울이...', '요즘 사는 맛.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '요즘 사는 맛 2', '고수리 김민철 김신지 무과수 스탠딩 에그 이랑 이연 이유미 임현주 정문정 정보 더 보기/감추기 고수리 김민철 김신지 무과수 스탠딩 에그 이랑 이연 이유미 임현주 정문정 정지우 정지음', '에세이', '음식 에세이', '15,300', '위즈덤하우스', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '멋있게 먹고 맛있게 살면 돼! 오늘도 열심히 살아낸 나를 위한 만찬 배민에서 2020년부터 발행 중인 뉴스레터 ‘주간 배짱이’(배민을 짱 좋아하는 이)의 푸드에세이를 모은 두 번째 책. 탄탄한 팬층을 보유하고 있는 에세이 작가 열한 명의 글에 이번 책에만 특별 참여한 이연 작가의 글을 더해 총 열두 명의 에세이 ...', '요즘 사는 맛 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '부부가 둘 다 잘 먹었습니다', '윤혜자', '에세이', '음식 에세이', '15,120', '몽스북', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '평범하고도 특별한 1년의 기록예쁜 집, 멋진 옷, 화려한 음식. SNS나 매체를 통해 구경할 수 있는 다른 사람의 삶은 잘 꾸며진 ‘한 때’의 모습인 경우가 많다. 그래서 그들의 삶이 부러워 보이지만 함부로 그들처럼 살 수 있을 거라 기대하기는 힘들었다. 이 책 『부부가 둘 다 잘 먹었습니다』는 매일의 소담한 한 끼 상...', '부부가 둘 다 잘 먹었습니다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '외로운 사람끼리 배추적을 먹었다', '김서령', '에세이', '음식 에세이', '13,500', '푸른역사', 1000, TO_DATE('2019년 01월', 'YYYY"년" MM"월"'), 1000, '이 책은 한 문장가가 세상에 흩뿌린 마지막 광휘이고, 한편으로는 그를 위한 기념비이기도 하다. 지난해 10월 세상을 떠난 김서령이 그간 음식과 관련해 썼던 글을 그러모은 그의 투병 막바지에서였다. 문학평론가 염무웅 선생의 말씀대로 보석처럼 반짝이는 조각글이 흩어져 사라져 가는 것을 막기 위해 시작된 편집은...', '외로운 사람끼리 배추적을 먹었다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '네가 맛있는 하루를 보내면 좋겠어', '츠지 히토나리 권남희', '에세이', '음식 에세이', '14,220', '니들북', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '『냉정과 열정 사이』 그 후 20년…싱글대디로 돌아온 츠지 히토나리의 가슴 뭉클한 가족 에세이* 아마존 재팬 분야 1위 / 출간 즉시 베스트셀러* 공지영·김중혁·오은·권남희·곽아람·이종혁·박준우 등 쏟아지는 찬사!그의 절망의 편린들에 울컥하다가 어느새 나는 감자를 깎고 양파를 볶고 토마토를 썰고 싶어졌다. 그러...', '네가 맛있는 하루를 보내면 좋겠어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '위로를 주는 빵집, 오렌지 베이커리', '키티 테이트 & 앨 테이트 이리나', '에세이', '음식 에세이', '19,800', '윌북(willbook)', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '2023년 가장 주목받는 힐링 에세이. 영국을 넘어 미국, 독일 등지에 출간되며 전 세계 독자들을 사로잡고 있는 감동 실화. 세상에서 가장 유명한 ‘빵 굽는 부녀’, 10대 베이커 키티 테이트와 아빠 앨 테이트의 이야기를 담은 책 『위로를 주는 빵집, 오렌지 베이커리』가 드디어 한국에 출간되었다. 출간 즉시 [타임스] ...', '위로를 주는 빵집, 오렌지 베이커리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '음식은 맛있고 인생은 깊어갑니다', '최갑수', '에세이', '음식 에세이', '14,850', '얼론북', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '애틋하고 짠하다가, 끝내는 즐겁다!다정한 음식, 따뜻한 인생 이야기박찬일(요리사·칼럼니스트), 김의성(배우), 레이먼 김(요리사) 추천!인생, 맛있게 지나갑니다.『어제보다 나은 사람』 『밤의 공항에서』 『우리는 사랑 아니면 여행이겠지』 『잘 지내나요, 내 인생』 등을 통해 섬세하고 투명한 문장으로 여행과 ...', '음식은 맛있고 인생은 깊어갑니다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '당신의 일상은 무슨 맛인가요', '오연서', '에세이', '음식 에세이', '13,950', '온더페이지', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '특별한 날이 아니어도, 엄청나게 맛있지는 않아도오늘도 우리에게 기운을 북돋고 용기를 주는 일상의 맛 이야기직장인의 최대 고민은 오늘 점심 메뉴라는 농담처럼, 세상의 모든 음식은 우리 삶의 희로애락을 고스란히 담고 있다. 물론 인생에 꽈배기처럼 달콤한 날만 있다면 좋겠지만, 살다 보면 닭발처럼 매운 날도 얼마...', '당신의 일상은 무슨 맛인가요.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '안주잡설', '정진영', '에세이', '음식 에세이', '12,600', '서랍의날씨', 1000, TO_DATE('2023년 01월', 'YYYY"년" MM"월"'), 1000, '이 책, 밤에 읽으면 위험하다.배우 박준면이 대한민국 모든 술꾼에게 던지는 매혹적인 한마디!당신의 술자리를 더 맛있게 빛낼 소설가 정진영의 30가지 안주썰!이 책은 우리의 술자리가 있을 때마다 빠지지 않고 식탁 위에 등장하지만, 술맛에 밀려 그 중요성이 크게 부각되지 않고 있는 다양한 안주의 매력을 저자만의...', '안주잡설.jpg');
