INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '와인에 몹시 진심입니다만,', '임승수', '에세이', '음식 에세이', '13,320', '수오서재', 1000, TO_DATE('2021년 03월', 'YYYY"년" MM"월"'), 1000, '와인을 마신 날, 안 마신 날, 어느 날이 더 기분 좋겠는가.맨땅에 헤딩해 체득한 가장 실질적이고 실용적인 와인 간증서여기 와인에 몹시 진심인 한 사람이 있다. 공학도 출신의 마르크스주의 책 쓰는 사회과학 작가인 저자는 호기심으로 한번 마셔본 와인에 홀딱 빠져 돌이킬 수 없는 가산탕진형 와인 애호가의 길에 ...', '와인에 몹시 진심입니다만,.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '소방관들을 위한 특별한 한 끼', '강제규', '에세이', '음식 에세이', '11,700', '책나물', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '엄마 배지영이 쓴 에세이 『소년의 레시피』에서야간자율학습 대신 가족의 저녁밥 차리던 소년 ‘강제규’.청년이 된 그가 119안전센터 소방관들을 위한 요리사가 되었다!소방관들은 누가 해준 밥을 먹고 지낼까? 갑자기 울리는 출동 벨, 1초가 아까운 구조환경 탓에 컵라면을 자주 먹을지도 모른다. ‘소방복무요원’이던 ...', '소방관들을 위한 특별한 한 끼.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아무튼, 떡볶이', '요조', '에세이', '음식 에세이', '10,800', '위고', 1000, TO_DATE('2019년 11월', 'YYYY"년" MM"월"'), 1000, '아무 떡볶이나 잘 먹으며 살아온 평화롭고 단조로운 인생 가운데, 『아무튼, 떡볶이』작가이자 ‘책방무사’ 대표, 팟캐스트 진행자이기도 한 뮤지션 요조에게는 하나의 타이틀이 더 붙어야 한다. 바로 ‘엄마와 자신이 만든 음식 다음으로 많이 먹은 음식이 떡볶이인 사람’이다. 인간적으로 그동안 떡볶이를 너무 과잉 ...', '아무튼, 떡볶이.jpg');
