INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '밥상머리 인문학', '오인태', '에세이', '음식 에세이', '19,800', '궁편책', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '사람과 밥상에 대한 통찰을 찰진 감성으로 빚어 낸 오인태 시인의 맛있는 ‘밥상머리 인문학’『밥상머리 인문학』은 저자 오인태의 말과 행동, 글과 신념이 일정한 궤를 그리는 데서 시작한다. 교사, 장학사, 교육 연구사, 교육 연구관을 거쳐 지금은 지리산 청학동에서 초등학교 교장으로 재직 중인 그는 교육자이자 시인...', '밥상머리 인문학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '와인 사피엔스', '김준근', '에세이', '음식 에세이', '17,820', '바림(barim)', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '와인의 세계를 소믈리에가 좀 더 쉽고 유쾌하게 그리고 규칙 없이 해주는 와인보다 맛있는 와인 이야기 고급스러운 술로 기억되며, 근접하기 어려운 술이라고 생각했던 와인이 우리의 평범한 일상 속으로 들어왔다. 사랑하는 연인과 낭만적인 데이트 시간에, 한강에서 친구들과 함께하는 유쾌한 시간에도, 또는 중요한 비즈...', '와인 사피엔스.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '날빛둥이가 속삭인다', '아침나무 아희', '에세이', '음식 에세이', '18,000', '문빈', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '미니멀리스트는 행복할까? 비건을 하면 건강해질까? 그토록 원하던 사람과의 사랑은 영원할까? 내 안의 속삭임을 들을 수 없을 때 어김없이 삶에 대한 불만과 불평, 느닷없이 찾아오는 아픔, 사랑의 무지에서 벗어날 수 없다. 우리에게 진정 필요한 건 나를 이끄는 내면의 속삭임이다. 작가는 20년간 비건과 비움, 사랑에 ...', '날빛둥이가 속삭인다.jpg');
