INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파도를 넘어서 케이크', '이재연', '에세이', '음식 에세이', '13,500', '클', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '자신의 우주가 담긴 케이크를 만드는직장인 홈베이커의 개인적이고 복잡한 이야기황선우, 박선아 작가 강력 추천!어린 시절 미국식 디저트를 즐겨 먹으며 자란 작가는 한국에서 쉽게 구하거나 맛볼 수 없는 기억 속의 그것들을 찾다, 결국 본인의 입에 맞는 디저트를 먹기 위해 홈베이킹을 시작했다. 처음부터 쉬웠던 것은 ...', '파도를 넘어서 케이크.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '요리는 감이여', '51명의 충청도 할매들 주미자 이유자', '에세이', '음식 에세이', '15,300', '창비교육', 1000, TO_DATE('2019년 08월', 'YYYY"년" MM"월"'), 1000, '인생 만렙 충청도 할매들, 늦깎이로 글을 배워 생애 첫 책 『요리는 감이여』 출간!한글 학교에 다니는 충청도 할머니들이 손 글씨로 쓴 요리법을 책으로 엮었다. 본문에 나오는 요리는 레시피를 따르는 것이 아니라 감으로 하는 것이다.라는 구절을 활용해 책 제목을 지었다.(68쪽 소고기미역국, 122쪽 옻백숙 참고) ...', '요리는 감이여.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '돈까스를 쫓는 모험', '이건우', '에세이', '음식 에세이', '15,480', '푸른숲', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '‘돈까스’에 진심인 일본어 번역가가 들려주는 돈까스 탐방기이자 예찬. 저자 이건우는 ‘친숙하고, 다양하고, 재미있으며, 무엇보다 맛있는’ 음식인 돈까스에 빠져 2017년부터 블로그 ‘돈까스를 쫓는 모험’에 돈까스 품평을 써오고 있다. 이 책은 저자가 방문한 수백 곳의 돈까스집 중 엄선한 서울·경기 지역 돈까스집...', '돈까스를 쫓는 모험.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '하루의 끝, 위스키', '정보연', '에세이', '음식 에세이', '16,200', 'CABOOKS', 1000, TO_DATE('2019년 10월', 'YYYY"년" MM"월"'), 1000, '위스키를 하나도 모르던 사람(저자)이 하나씩 맛보며 알아간, 저자의 즐거운 위스키 여정과 직접 경험해 알게 된 ‘살아 있는’ 위스키 정보를 『하루의 끝, 위스키』에 담았다. 국내외 위스키 바와 위스키 증류소 현지 투어, 그리고 오랫동안 ‘위스키 스터디’를 진행하면서 직접 맛보고 느끼고 공부하며 체험한 120여 종...', '하루의 끝, 위스키.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아무튼, 술집', '김혜경', '에세이', '음식 에세이', '10,800', '제철소', 1000, TO_DATE('2021년 06월', 'YYYY"년" MM"월"'), 1000, '집이 내가 사는 곳이라면 술집은 나를 살게 하는 곳!위胃로 가는 위로를 건네는 전방위 술집 탐방기아무튼 시리즈의 마흔네 번째 이야기는 ‘술집’이다. 광고 기획자로 일하며 시 읽고 술 마시는 팟캐스트 [시시알콜]을 5년 넘게 진행해온 김혜경의 첫 단독 에세이집이기도 하다. 마치 식단 일기처럼 온통 먹고 마신 하...', '아무튼, 술집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오늘 조금 더 비건', '초식마녀', '에세이', '음식 에세이', '13,050', '채륜서', 1000, TO_DATE('2020년 04월', 'YYYY"년" MM"월"'), 1000, '가볍고 유쾌한 비건 요리 에세이 만화작가 초식마녀는 채식하는 일반 직장인으로 집에서 만들어 먹은 비건 요리를 네 컷 만화로 그렸다. 차곡차곡 쌓인 기록들이 쉽고 맛있는 비건 레시피로 입소문이 나면서 큰 인기를 얻었다. 이제 대표적인 비건 유튜버로 꼽히는 그녀의 인스타그램 속 비건 레시피와 일상을 세 끼 식단으...', '오늘 조금 더 비건.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '빵 고르듯 살고 싶다', '임진아', '에세이', '음식 에세이', '11,250', '휴머니스트', 1000, TO_DATE('2018년 06월', 'YYYY"년" MM"월"'), 1000, '안녕하세요. 오늘도 빵, 하셨나요?일러스트레이터 임진아가 일상에서 발견한 행복의 조각들.우리는 지금 고소한 냄새가 풍기는 빵집에 들어섰습니다. 빈 쟁반에 새하얀 유산지를 깔고, 조금은 비장한 표정으로 설렘을 품고 빵 집게를 쥐어요. 그리고 빵들 앞에 섭니다. 식빵, 크림빵, 치아바타, 소보로빵부터 예쁘고 화...', '빵 고르듯 살고 싶다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '소년의 레시피', '배지영', '에세이', '음식 에세이', '12,150', '웨일북', 1000, TO_DATE('2017년 06월', 'YYYY"년" MM"월"'), 1000, '이토록 매력 터지는 소년이라니!EBS [지식채널e] 화제의 방영작 [소년의 레시피] 자신의 꿈을 향해 묵묵히 즐기며 노력하는 모습이 마음의 울림을 준다.남들이 가지 않은 길, 남들이 안 된다고 말하는 길이라 해도 자신의 신념을 꿋꿋히 지키고 묵묵히 걸어가는 모습이 정말 멋지다.항상 현실을 핑계 대며 무엇이...', '소년의 레시피.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '귀여워서 또 보게 되는 물고기도감', '임현 김지민', '에세이', '음식 에세이', '15,120', '브레인스토어(BRAINstore)', 1000, TO_DATE('2021년 05월', 'YYYY"년" MM"월"'), 1000, '우리나라 삼면 바다, 네 개의 계절이 선물해 준 풍부한 수산물의 세계를귀여운 그림으로 꽉 채워 넣은 물고기도감제철 수산물의 특징부터 맛있게 먹는 방법까지알아두면 일 년이 꽤 행복해질 현대판 자산어보『귀여워서 또 보게 되는 물고기도감』은 한반도를 둘러싼 사계절 수산물을 귀여운 그림과 심플하면서도 꽤 풍부한...', '귀여워서 또 보게 되는 물고기도감.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '텐동의 사연과 나폴리탄의 비밀', '네모', '에세이', '음식 에세이', '15,750', '휴머니스트', 1000, TO_DATE('2021년 09월', 'YYYY"년" MM"월"'), 1000, '먹는 것에 진심인 당신을 위해일본인 친구 ‘네모’가 들려주는 본격 식탁 에세이알고 먹으면 더 맛있습니다!텐동의 잔잔한 사연과 나폴리탄의 소소한 비밀, 그리고 이런저런 일본 음식들에 관한 이야기를 담은 책이다. 일본인 저자가 한글로 써서 화제를 모았던 『진짜 도쿄 맛집을 알려줄게요』에 이은 두 번째 책으로, ...', '텐동의 사연과 나폴리탄의 비밀.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '생강빵과 진저브레드', '김지현 최연호', '에세이', '음식 에세이', '13,320', '비채', 1000, TO_DATE('2020년 03월', 'YYYY"년" MM"월"'), 1000, '상상만 했던 소설 속 음식들,이를 우리말로 옮기는 ‘번역의 맛’에 대하여소공녀 세라, 하이디, 작은 아씨들, 주디 애벗……. 책을 사랑하는 독자라면 한번쯤 그들의 친구가 되어 고민을 털어놓고 웃음과 눈물을 나누었을 것이다. 그리고 건포도빵과 나무딸기 주스, 그레이비 같은 들어본 적조차 없는 음식의 맛을 황홀하...', '생강빵과 진저브레드.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '그럴수록 요리', '네코자와 에미 최서희', '에세이', '음식 에세이', '14,400', '언폴드', 1000, TO_DATE('2022년 10월', 'YYYY"년" MM"월"'), 1000, '고양이처럼 유연하게 인생을 풍요롭게살아가기 위한 힌트가 담긴 요리+인생 에세이뮤지션이자 작가, 편집장, 영화평론가 등 다양한 분야에서 활약하며 많은 일본 여성들에게 사랑받고 있는 네코자와 에미. 맛있는 음식을 요리하며 삶의 기쁨과 위로를 얻는다는 작가는 50대에 접어들자 삶의 태도를 새롭게 가다듬으며 인생...', '그럴수록 요리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '응답하라 우리 술', '김승호', '에세이', '음식 에세이', '18,000', '깊은샘', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '우리 술(막걸리, 소주)의 전통 제조과정과 조선시대 시인묵객들의 술에 얽힌 흥미로운 애주사(愛酒史)를 민속·풍속적이며 미생물과학으로까지 전개한 술 인문역사교양서 『응답하라 우리 술』이 도서출판 깊은샘에서 출간되었다.저자 김승호는 ’90년대 문화답사 베스트셀러 저서에서 한국인들에게 회자됐던 ‘아는 만큼 보...', '응답하라 우리 술.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나를 채우는 한 끼', '임성용 김지은', '에세이', '음식 에세이', '19,800', '책장속북스', 1000, TO_DATE('2023년 01월', 'YYYY"년" MM"월"'), 1000, '1,000만 조회수의 연재 시리즈!「임성용의 보약밥상」이 감성 더한 음식 에세이로 돌아왔다!오늘의 컨디션은 어떤가요?바쁜 현대사회를 살아가며 마음이 허기지고 몸이 지친 이들을 위해 현직 한의사가 발 벗고 나섰다. 2년간 1,000만 이상의 누적 조회수를 기록하며 독자들에게 사랑받아온 레이디경향 연재 시리즈 「...', '나를 채우는 한 끼.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '밥 한번 먹자는 말에 울컥할 때가 있다', '위영금', '에세이', '음식 에세이', '15,300', '들녘', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '나는 별 같은 이 밥을 먹으려고 태어났나 봐!함경도에서 담아온 주렁진 그리움으로맛과 기억을 요리하다2022년 남북통합문화콘텐츠 창작지원 공모 선정작먹고살기 위해 떠나온 지 25년,맛과 기억을 요리하며 떠올린 아롱진 나날들어떻게 지내? 우리 밥 한번 먹어야지.때로는 건성으로 건네는 한마디에 눈물이 핑 돌...', '밥 한번 먹자는 말에 울컥할 때가 있다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '문과 남자의 과학 공부', '유시민', '인문', '인문/교양', '15,750', '돌베개', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '과학 공부로 길어 올린 생명과 우주에 관한 진실, 인간과 사회를 이해하는 방법내 삶을 어떤 의미로 채울 것인가? 지금 여기, 지식과 교양의 새로운 패러다임 과학의 사유와 인문학의 성찰이 함께하는 지적 여정 문과도 과학적으로 생각할 수 있다! 『문과 남자의 과학 공부』는 역사 · 정치 · 경제 · 글쓰기 · ...', '문과 남자의 과학 공부.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '모든 삶은 흐른다', '로랑스 드빌레르 이주영', '인문', '인문/교양', '15,120', '피카(FIKA)', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '프랑스 아마존 베스트셀러최재천, 이해인, 윤대현, 장재형 등 각계 명사들의 강력 추천프랑스 현지 언론의 압도적 찬사이 책은 흐르는 삶 속에서 ‘자기 자신’이라는 유일한 섬이 되는 길을 안내한다. 삶은 내가 내 의지대로 살아가는 게 아니라 그저 흘러가며 살아지는 것이다._ 최재천(이화여대 에코과학부 석좌교수...', '모든 삶은 흐른다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '내면소통', '김주환', '인문', '인문/교양', '29,700', '인플루엔셜', 1000, TO_DATE('2023년 02월', 'YYYY"년" MM"월"'), 1000, '나와 세상을 바꾸는 힘은 불굴의 의지가 아니라 실패를 두려워하지 않는 마음근력에서 나온다.『회복탄력성』김주환 교수가 최신 뇌과학과 명상으로 안내하는 모든 두려움으로부터 완전히 자유로워지는 법 ★ 최재천, 권오현, 채정호, 김봉진, 김대식, 김미경, 장동선, 최재붕 한국 최고의 학계·경영계 리더 8인의 강력...', '내면소통.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '유현준의 인문 건축 기행', '유현준', '인문', '인문/교양', '17,550', '을유문화사', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '건축물은 인간의 생각과 세상의 물질이 만나 만들어진 결정체로, 많은 자본이 드는 만큼 여러 사람의 의견이 일치할 때만 완성되는 그 사회의 반영이자 단면이다. 그렇기에 건축물을 보면 당대 사람들이 세상을 읽는 관점, 물질을 다루는 기술 수준, 사회 경제 시스템, 인간에 대한 이해, 꿈꾸는 이상향, 생존을 위한 몸부...', '유현준의 인문 건축 기행.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '노회찬 평전', '이광호 평등하고 공정한 나라 노회찬재단', '인문', '인문/교양', '20,700', '사회평론아카데미', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '타인의 존엄성을 지키고자불평등에 맞서 싸운 정의로운 정신‘함께 사는 세상’을 향한 노회찬의 삶과 꿈사회적 불평등에 맞서고 인간의 존엄성을 지키기 위해 평생을 바친 노회찬의 삶을 집대성한 『노회찬 평전』이 출간되었다. 이 책을 기획한 노회찬재단은 노회찬의 말과 글, 행적을 모아 ‘노회찬 아카이브’를 구성하...', '노회찬 평전.jpg');
