INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '현지에서 바로 쓰는 시원스쿨 여행영어', '이시원', '여행', '여행회화', '10,350', '시원스쿨닷컴', 1000, TO_DATE('2019년 07월', 'YYYY"년" MM"월"'), 1000, '비가 와도 젖지 않는 워터프루프 소재!짐이 많아도 한 손에 간편히 들 수 있는 포켓북 판형!영어를 몰라도 바로 말할 수 있는 한글 발음!쉽고 빠르게 상황을 찾을 수 있는 날개 인덱스!언제 어디서나 쓰는 기본표현+만능으로 쓸 수 있는 핵심패턴+50개의 다양한 여행 상황으로 여행뿐 아니라 일상생활에서도 당당하게 영어...', '현지에서 바로 쓰는 시원스쿨 여행영어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '여행영어 무작정 따라하기', '라이언', '여행', '여행회화', '9,000', '길벗이지톡', 1000, TO_DATE('2017년 06월', 'YYYY"년" MM"월"'), 1000, '말이 통하면 여행의 품격이 달라진다해외여행 경험이 부족해도, 영어에 자신 없어도 여행 영어 무작정 따라하기 한 권이면 걱정 없이 떠난다출국부터 귀국까지 여행 영어의 빅 데이터해외여행이 낯설어도, 영어를 못해도 걱정 없다해외에서 일어날 수 있는 모든 상황을 예측해 꼭 한 번은 활용하게 될 실제 표현만 골라 모...', '여행영어 무작정 따라하기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '마구로센세의 여행 일본어 마스터', '최유리 나인완', '여행', '여행회화', '14,400', '브레인스토어(BRAINstore)', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '이제 다시 떠나는 일본 여행, 『마구로센세의 여행 일본어 마스터』와 함께!비행기, 공항, 대중교통, 거리, 관광지, 호텔, 식당, 쇼핑, 긴급상황??여행의 모든 상황을 미리미리 예습하고 떠난다!이 책 한 권이 여행지에서의 모든 우여곡절을 막아낼 수는 없겠지만, 예기치 않은 사고가 해프닝 정도로 작아질 수 있도록 적절...', '마구로센세의 여행 일본어 마스터.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '청춘 영어: 여행회화', '배진영 강민정', '여행', '여행회화', '12,420', '다락원', 1000, TO_DATE('2017년 08월', 'YYYY"년" MM"월"'), 1000, '배우고자 하는 마음은 언제나 청춘이신 어르신을 위한『청춘 영어』 시리즈의 ‘여행회화’ 편뉴욕, 런던, 파리, 시드니 등등… 세상은 정말 넓고, 그만큼 가고 싶은 곳도 다양하다. 하지만 해외여행 갈 때 영어 때문에 고민하는 분들도 참 많습니다. 『청춘 영어: 여행회화』는 이처럼 영어가 걱정돼서 해외여행 가기 망설...', '청춘 영어: 여행회화.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '미식가를 위한 일본어 안내서', '황국영 서평화', '여행', '여행회화', '12,600', '휴머니스트', 1000, TO_DATE('2021년 02월', 'YYYY"년" MM"월"'), 1000, '미식 여행하면서 일본어 입문합니다.일본 음식의 거의 모든 목록과 일본어 기초가 만났다. 일본어 0레벨도 즐길 수 있는, 오직 먹는 여행을 위한 최소한의 일본어. 식재료와 음식 단어들로 일본어 글자를 익히는 ‘준비 운동’을 마치고 나면, 스시, 라멘에서 이자카야, 야키토리까지 15가지 분류에 따라 주요 메뉴의 이름...', '미식가를 위한 일본어 안내서.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '현지에서 바로 쓰는 시원스쿨 여행 베트남어', '시원스쿨베트남어연구소', '여행', '여행회화', '10,350', '시원스쿨닷컴', 1000, TO_DATE('2020년 03월', 'YYYY"년" MM"월"'), 1000, '비가 와도 젖지 않는 워터프루프 소재!짐이 많아도 한 손으로 간편하게 들 수 있는 포켓북 사이즈!베트남어를 몰라도 바로 말할 수 있는 한국어 발음 제공!쉽고 빠르게 상황별로 찾을 수 있는 날개 인덱스!언제 어디서나 쓰이는 기본 표현,상황에 따라 꺼내 쓰는 여행 상황별 패턴,알아 두면 반드시 써먹는 추가 핵심패턴까...', '현지에서 바로 쓰는 시원스쿨 여행 베트남어.jpg');
