INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '토마스와 앤더스의 착한 여행영어회화', '토마스 프레드릭슨 앤더슨 프레드릭슨', '여행', '여행회화', '13,500', '진명출판사', 1000, TO_DATE('2016년 05월', 'YYYY"년" MM"월"'), 1000, '글로벌 세계탐험의 파트너가 되어줄‘토마스와 앤더스의 착한여행영어회화’는 비행기를 타거나, 호텔을 선택하거나,쇼핑을 하거나 또는 병원이나 약국을 방문하는 상황들만을 위해서가 아닌, 광범위한 구문과 표현듣,각 문화나 에티켓에 대한 유용한팁들, 그리고 저자가 실제로 겪었던 여행담까지... 이 모두를 어울러 당...', '토마스와 앤더스의 착한 여행영어회화.jpg');
