INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '리얼토킹 트래블', 'Ellie Oh Tasia Kim 이다', '여행', '여행회화', '9,900', '뉴런(NEWRUN)', 1000, TO_DATE('2020년 12월', 'YYYY"년" MM"월"'), 1000, '지구에서 가장 많이 팔린 영어책‘잉글리시 리스타트’의 여행 회화편!인종, 국적 상관없이 영어를 공부하는 사람이라면 누구나 함께 보는 세계인의 영어책 ‘잉글리시 리스타트’ 시리즈! 이 책의 여행 회화편 『리얼토킹 트래블』이 출간되었다. 기내에서, 호텔에서, 레스토랑에서, 쇼핑할 때, 관광할 때 등 여행할 때 필...', '리얼토킹 트래블.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '포켓북 왕초보 여행 영어회화', '이서영', '여행', '여행회화', '9,720', '랭컴(LanCom)', 1000, TO_DATE('2018년 01월', 'YYYY"년" MM"월"'), 1000, '내 손에 펼쳐진 여행회화 바로 찾아 당당하게 써먹는다!들고 다니면서 하고 싶은 말을 바로 찾아볼 수 있는 포켓북이며 여행에서 많이 쓰이는 간편하고 유용한 표현만을 엄선하였다. 원하는 표현을 알기 쉽게 다양한 그림으로 상황을 묘사하였고 여행 스케줄에 맞춰 기본표현부터 출국, 숙박, 외출, 관광, 식사, 방문, 쇼핑...', '포켓북 왕초보 여행 영어회화.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '미니북 백퍼 여행 일본어', '임단비 구라모토 타에코', '여행', '여행회화', '8,820', '라즈베리', 1000, TO_DATE('2017년 12월', 'YYYY"년" MM"월"'), 1000, '일본 여행 계획 있다면 꼭 구비해야 하는 필수 아이템 1권1. 일본어를 몰라도 손가락으로 짚으면 끝.2. 일본어를 몰라도 손가락으로 짚어 달라고 하면 끝.3. 일본어를 공부하고 싶은 의욕이 샘솟을 땐 팟캐스트 강의를 들으면 끝.', '미니북 백퍼 여행 일본어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나 혼자 간다! 여행 터키어', '장주영', '여행', '여행회화', '12,600', '문예림', 1000, TO_DATE('2017년 03월', 'YYYY"년" MM"월"'), 1000, '이 책 한 권만 있으면 나 혼자 터키 여행 갈 수 있다터키 사람들은 느긋하게 차 마시기를 좋아한다. 뭐든 빨리빨리 해야 하는 우리나라 일상에 지친다면, 오랜 세월 중앙아시아 초원에서부터 광대한 오스만제국을 지배했던 매력적인 나라 터키로 떠나 보자.여행을 한다거나 또는 학교에 다니거나 일을 하게 됐을 때도 이 책...', '나 혼자 간다! 여행 터키어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '랜드마크 일본어회화', '정문주', '여행', '여행회화', '12,240', 'Pub.365', 1000, TO_DATE('2017년 11월', 'YYYY"년" MM"월"'), 1000, '일본 어느 지역으로 떠나볼까?일본의 50개소 랜드마크를 지도에 콕콕! 표시해 두어 여행하듯 쉽게 공부할 수 있게 만든 일본어 회화책! 50개의 회화 패턴으로 실생활에서도 응용하며 사용 가능한 문장들을 담았다. 50개소 랜드마크를 여행하듯 공부하면 어느새 일본어가 입에서 술술~ 나온다.', '랜드마크 일본어회화.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '에이든 국내여행 가이드북', '타블라라사 이정기', '여행', '국내여행', '16,920', '타블라라사', 1000, TO_DATE('2022년 04월', 'YYYY"년" MM"월"'), 1000, '여행 베스트셀러 에이든 국내여행 가이드북 2022년 4월 개정증보판을 정식 출간하였습니다. 48개나 되는 지도가 전체 업데이트 되었고 최근 핫한 스팟들과 도별 맛집이 추가 되었습니다. 물론 사진과 컨텐츠도 전체적으로 업데이트 되었습니다. 에이든 국내여행 가이드북 개정증보판은 모바일 시대에 여행계획을 빠...', '에이든 국내여행 가이드북.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '여기 가려고 주말을 기다렸어', '주말랭이', '여행', '국내여행', '17,820', '빅피시', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '‘이번 주말에 뭐 하지’ 고민은 이제 끝!_ 지금 안 가보면 후회하는 숨겨진 국내 핫플레이스가 한 권에평일에 지친 일상을 보낸 사람들에게 주말의 즐거움을 기대하게 하는 여행 뉴스레터가 있다. 바로 매주 금요일 발행되는 ‘주말랭이.’ 비슷한 규모의 뉴스레터와 비교해 평균 오픈율이 3배, 클릭률이 10배, 그리고 뉴...', '여기 가려고 주말을 기다렸어.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '에이든 제주여행 가이드북', '이정기 타블라라사', '여행', '국내여행', '15,750', '타블라라사', 1000, TO_DATE('2021년 07월', 'YYYY"년" MM"월"'), 1000, '에이든 제주여행 가이드북은 여행자들이 에이든 여행지도를 만들면서 수집했던 전국의 수천개 콘텐츠를 가지고 고르고 골라서 통합본으로 제작했다. 지도와 같이 전체를 한눈에 볼 수 있지는 못하나 그 내용이 제주여행 가이드북안에 모두 담고 있어 소장하거나 집에서 갈만한 곳을 찾기에 좋다. 에이든 여행지도의 다른 지...', '에이든 제주여행 가이드북.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '교과서가 쉬워지는 주말여행', '김수진 박은하', '여행', '국내여행', '19,800', '길벗', 1000, TO_DATE('2022년 05월', 'YYYY"년" MM"월"'), 1000, '코로나로 몸과 마음이 힘든아이와 함께 교과서 주말여행을 떠나자!몇 년째 계속되는 코로나에 몸과 마음이 힘든 아이들이 많습니다. 학교를 가지 못하고 온라인 수업이 계속되면서 주의력 결핍은 물론이고 학습 성취도까지 바닥인 경우가 많습니다. 외부와의 상호작용을 제대로 하지 못해 과잉행동장애나 정서 장애까지 생...', '교과서가 쉬워지는 주말여행.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '하루쯤 나 혼자 어디라도 가야겠다', '장은정', '여행', '국내여행', '14,220', '북라이프', 1000, TO_DATE('2021년 09월', 'YYYY"년" MM"월"'), 1000, '혼자서 하루쯤 어디라도 떠나고 싶어지는 날이 있다. 이런 때 떠나는 잠깐의 여행은 마음의 큰 위안이 되고 돌아온 일상을 잘 살아내게 하는 힘이 된다. 이 책은 떠나고 싶은 마음이 불쑥 찾아올 때 코로나19 상황에서도 안전하게 갈 수 있는 작은 여행을 선물한다. 지치고 아무 의욕도 없는 공허한 마음에 기분 전환이 되...', '하루쯤 나 혼자 어디라도 가야겠다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이국적인 국내여행지 64', '이환수', '여행', '국내여행', '15,300', '책밥', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '한스포토가 추천하는 해외보다 더 좋은 우리나라 여행지최근 몇 년 안전을 위해 우리는 실외 활동을 자제하고 최소한의 동선으로 살았다. 덕분에 조금 안전하게 살았지만 조금 덜 행복하기도 했다. 제한된 삶에서 벗어나 조금 더 자유롭고 조금 더 낯선 풍경 속에 존재하고 싶다면, 나라 밖 풍경을 구경하고 싶지만, 여전히...', '이국적인 국내여행지 64.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '에이든 우리나라 전국 여행지도', '타블라라사 이정기', '여행', '국내여행', '16,830', '타블라라사', 1000, TO_DATE('2022년 02월', 'YYYY"년" MM"월"'), 1000, '에이든 우리나라 전국 여행지도는 여행자들이 어느 패턴으로 이동하는지 통계와 리서치를 근거로 제작하였다. 또한 많이 가는 여행지 뿐만아니라 최근에 여행 패턴의 흐름에 따라 캠핑, 맛집, 카페, 인스타스팟 컨텐츠를 대거 추가하였다. 방수지도라는 특수 수입지를 사용하였으며 접지와 포장등 어느정도 자동화 되어있는...', '에이든 우리나라 전국 여행지도.jpg');
