INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'Go 언어를 활용한 분산 서비스 개발', '트래비스 제프리 정현석', 'IT 모바일', '네트워크/해킹/보안', '24,300', '제이펍', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '분산 서비스 밑바닥부터 구축하고 나의 Go 능력은 하늘을 뚫고Go 기본은 뗐고 스킬을 한 차원 높이고 싶다면, Go가 가장 빛나는 분야인 분산 서비스 구축에 도전해보자. 이 책은 gRPC 분산 서비스를 설계, 개발, 배포하는 방법을 자세히 살펴본다. 스토리지 처리의 기본 요소에서 시작해 클라이언트 및 서버 네트워킹을 숙...', 'Go 언어를 활용한 분산 서비스 개발.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '솔리디티 프로그래밍 완벽 가이드', '조대환', 'IT 모바일', '네트워크/해킹/보안', '28,800', '위키북스', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '개발 경험이 없어도 쉽게 블록체인과 솔리디티를 이해할 수 있습니다!솔리디티는 스마트 컨트랙트를 구현하는 언어이며, 스마트 컨트랙트는 블록체인에서 작동하는 프로그램이다. 『솔리디티 프로그래밍 완벽 가이드』는 블록체인의 기본적인 개념과 솔리디티로 스마트 컨트랙트를 개발하는 법을 자세히 알려준다. 이 책에...', '솔리디티 프로그래밍 완벽 가이드.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '그림으로 공부하는 TCP/IP 구조', '미야타 히로시 김모세', 'IT 모바일', '네트워크/해킹/보안', '27,000', '제이펍', 1000, TO_DATE('2021년 10월', 'YYYY"년" MM"월"'), 1000, '인터넷, TCP/IP, 애플리케이션부터 보안, 네트워크 기기, 부하분산까지네트워크의 기본 지식을 한 권에 담았다!네트워크는 본질적으로 커뮤니케이션이고, 커뮤니케이션은 성질이 다른 여러 연결 요소를 묶어내는 규칙 또는 규약으로 만들어진다. 네트워크에 조금이나마 관심이 있던 분들이라면 OSI 7 레이어와 같이 다소 복...', '그림으로 공부하는 TCP IP 구조.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '리버싱 핵심 원리', '이승원', 'IT 모바일', '네트워크/해킹/보안', '43,200', '인사이트(insight)', 1000, TO_DATE('2012년 09월', 'YYYY"년" MM"월"'), 1000, '리버서라면 꼭 알아야 할 핵심 원리를 모두 담았다리버싱이란 프로그램의 내부를 깊이 들여다보고 조작할 수 있는 기법이다. 이는 우리가 흔히 사용하는 상용 프로그램 등에도 가능하기 때문에 해킹의 방법으로 응용될 수 있다. 동시에 리버싱은 일명 ‘리버서’라 불리는 악성 코드 분석가들의 특기이며, 리버싱을 잘 활용...', '리버싱 핵심 원리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파인만의 물리학 강의 3 양자역학', '리처드 필립 파인만 정재승', '자연과학', '물리학', '28,500', '승산', 1000, TO_DATE('2009년 05월', 'YYYY"년" MM"월"'), 1000, '『파인만의 물리학 강의』 시리즈 완간『파인만의 물리학 강의』시리즈 제3권, 양자역학 편.『파인만의 물리학 강의』시리즈는 1961년 가을부터 1963년 5월까지 리처드 파인만이 캘리포니아 공과대학에서 진행한 강의를 로버트 레이턴과 매슈 샌즈가 정리해서 글로 옮긴 것이다. 일반 물리학을 다룬 시리즈 제1권과 전자기...', '파인만의 물리학 강의 3 양자역학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '물리의 정석 : 고전 역학 편', '레너드 서스킨드 조지 라보프스키 이종필', '자연과학', '물리학', '16,650', '사이언스북스', 1000, TO_DATE('2017년 08월', 'YYYY"년" MM"월"'), 1000, '호킹을 이긴 위대한 물리학자의진짜 물리학스탠퍼드 대학교의 명강을 완벽 수록(유튜브 누적 조회수 1000만 회)《월 스트리트 저널》 선정 ‘최고의 책’파인만의 『물리학 강의』를 잇는 21세기의 필수 교양올해 7월 유럽 입자 물리 연구소의 대형 강입자 충돌기(Large Hadron Collider, LHC) 실험을 통해 새로운 입자가 ...', '물리의 정석   고전 역학 편.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '한 번 읽으면 절대 잊을 수 없는 물리 교과서', '이케스에 쇼타 이선주', '자연과학', '물리학', '15,300', '시그마북스', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '스토리로 이해하는지금까지 없었던 물리 입문서이 책은 물리 참고서이기도 하지만 읽을거리이기도 하다. 이 책에서는 물리의 각 단원 해설과 함께 과학자의 이름과 공식의 배경을 잘 구성해 넣어서 이야기가 잘 떠오르도록 했다. 그렇게 해서 이 책은 물리의 기초를 이해하기 위한 참고서 역할을 함과 동시에 위대한 과학의...', '한 번 읽으면 절대 잊을 수 없는 물리 교과서.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '물질의 재발견', '정세영 박용섭 양범정 최형준 최형순 신용일 김튼튼 고재현 한정훈 김기덕 정보 더 보기/감추기 정세영 박용섭 양범정 최형준 최형순 신용일 김튼튼 고재현 한정훈 김기덕 박성찬', '자연과학', '물리학', '17,820', '김영사', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '이미 안다고 생각했던 물질들의 놀라운 반전각 분야 국내 최고의 학자들과 함께하는 물질물리학 오디세이금속, 자석, 유리처럼 너무나 흔하고 평범한 물질에서부터 많이 들어봤지만 설명하기는 어려운 반도체와 부도체, 아직 해결되지 못한 물리학의 두 난제 초전도체와 암흑물질까지, 11가지 물질을 통해 물리학의 최전선...', '물질의 재발견.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '우발과 패턴', '마크 뷰캐넌 김희봉', '자연과학', '물리학', '14,400', '시공사', 1000, TO_DATE('2014년 08월', 'YYYY"년" MM"월"'), 1000, '인간의 역사에서 예측할 수 없는 현상은 무수히 많이 벌어져왔다. 2011년 동일본 대지진, 2010년 아이티 대지진, 2008년 쓰촨성 대지진 등, 세계의 곳곳에서 발생하는 지진은 예상하지 못한 시기에 예상하지 못한 크기로 찾아와 지구를 아프게 한다. 도대체 이런 일들은 왜 아무런 예고도 없이 갑자기 벌어지는 걸까? 우리...', '우발과 패턴.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '만약 시간이 존재하지 않는다면', '카를로 로벨리 김보희 이중원', '자연과학', '물리학', '14,400', '쌤앤파커스', 1000, TO_DATE('2021년 05월', 'YYYY"년" MM"월"'), 1000, '만약 시간이 존재하지 않는다면,우리가 살고 있는 이 세상과 우주를 어떻게 이해할 수 있을까?견고한 기존의 관념들을 뒤엎고‘시간 없이’세상을 바라보는 카를로 로벨리의 특별하고 감각적인 물리학 모험『모든 순간의 물리학』, 『보이는 세상은 실제가 아니다』, 『시간은 흐르지 않는다』로 우리에게 잘 알려진 세...', '만약 시간이 존재하지 않는다면.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '물리의 정석 : 양자 역학 편', '레너드 서스킨드 아트 프리드먼 이종필', '자연과학', '물리학', '19,800', '사이언스북스', 1000, TO_DATE('2018년 05월', 'YYYY"년" MM"월"'), 1000, '당신의 마음속 물리학자를 깨워 줄 단 하나의 양자 역학 강의이제 양자 역학은 상식이 되고 있다. 가수 박진영, 나르샤, 그리고 방송인 김제동 등이 텔레비전 방송에 출연해 양자 역학에 대해 서슴없이 말한다. ‘슈뢰딩거의 고양이’에서 유래해, 역설적인 상황을 빗대어 표현하는 말 ‘슈뢰딩거의 ○○○’은 사람들이 보...', '물리의 정석   양자 역학 편.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아날로그 사이언스', '윤진 이솔 최준곤', '자연과학', '물리학', '15,120', '해나무', 1000, TO_DATE('2019년 01월', 'YYYY"년" MM"월"'), 1000, '『아날로그 사이언스: 만화로 읽는 양자역학』은 쉽게 양자역학을 설명해주는 친절하고 감성적인 과학 카툰 책이다. 양자역학이 왜 등장했는지, 양자역학이란 도대체 무엇인지, 왜 아인슈타인과 보어가 격렬한 논쟁을 펼쳤는지 등을 과학사 속 흥미로운 에피소드와 함께 풀어낸다. 간결하면서도 유머러스한 그림에 담백하고...', '아날로그 사이언스.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아는 만큼 보이는 세상 : 물리편', '송경원 김범준 가와무라 야스후미', '자연과학', '물리학', '14,400', '유노책주', 1000, TO_DATE('2023년 02월', 'YYYY"년" MM"월"'), 1000, '공기 속에 다이아몬드가 떠다닌다?기둥 없는 다리가 왜 무너지지 않을까?물리는 일상 속 궁금증에 명쾌한 답변을 줍니다!tvN [어쩌다 어른] [책 읽어주는 나의 서재] 출연베스트셀러 『세상물정의 물리학』, 『관계의 과학』 저자 김범준 추천!비 오는 날 우산 표면을 타고 또르르 흐르는 물방울을 보면 문득 이런 의문...', '아는 만큼 보이는 세상   물리편.jpg');
