INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '뉴턴의 프린키피아', '안상현', '자연과학', '물리학', '14,400', '동아시아', 1000, TO_DATE('2015년 12월', 'YYYY"년" MM"월"'), 1000, '아름다운 원뿔곡선의 기하학으로 이해하는 만유인력의 법칙!뉴턴이 떨어지는 사과에서 본 것은 다름 아닌 ‘달’이었다!달이 지구를, 지구가 태양을 타원 궤도로 도는 것이 만유인력의 법칙 때문이라고?뉴턴은 만유인력의 법칙을 어떻게 밝혀낸 것일까?근대 과학혁명을 이끈 인류 최고의 고전 프린키피아국내 필자가 제대로...', '뉴턴의 프린키피아.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파인만의 물리학 강의 2', '리처드 필립 파인만 박병철', '자연과학', '물리학', '38,000', '승산', 1000, TO_DATE('2006년 09월', 'YYYY"년" MM"월"'), 1000, '리처드 파인만이 1961년부터 2년간 학부생들을 대상으로 강의한 내용을 편집하여 책으로 엮은 것이 바로 『파인만의 물리학 강의』이다. 파인만의 전설적인 강의가 책으로 나온 지 이미 40년 이상이 지나면서 물리적 세계에 대한 우리의 이해에는 많은 변화가 있었지만, 『파인만의 물리학 강의』는 그러한 세파를 견뎌 냈...', '파인만의 물리학 강의 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '세상에서 가장 쉬운 재미있는 물리', '미사와 신야 장재희 송미란', '자연과학', '물리학', '16,020', '미디어숲', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '문송한 세상, 자녀의 이과 지능을 깨워라!복잡한 수식이 안 나오는, 세상에서 가장 쉬운 물리 수업현직 교사가 학생들의 궁금증을 모아모아 생활 밀착 소재로 알려주는 흥미진진 물리의 세계이 책의 저자는 누구나 품을 법한 세상만사에 관한 호기심을 시작으로 복잡한 수식 없이도 자연스럽게 독자를 물리의 세계로 안...', '세상에서 가장 쉬운 재미있는 물리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '초공간', '미치오 카쿠 박병철', '자연과학', '물리학', '22,500', '김영사', 1000, TO_DATE('2018년 06월', 'YYYY"년" MM"월"'), 1000, '자연은 근본적인 단계로 갈수록 단순해진다!고차원 시공간에서 완성되는 자연법칙의 통일『평행우주』 『불가능은 없다』 미치오 카쿠가 풀어내는 현대물리학의 차원 혁명이론물리학계의 세계적 석학, 미래학자, 과학 엔터테이너 미치오 카쿠와 함께하는 초공간 여행. 『초공간』은 상대성이론에서 양자역학, 블랙홀, 웜홀,...', '초공간.jpg');
