INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '감성물리', '김광석', '자연과학', '물리학', '21,000', '부산대학교출판문화원', 1000, TO_DATE('2021년 07월', 'YYYY"년" MM"월"'), 1000, '초보자들에게 과학 지식과의 첫 만남은 어딘가 상식이나 감성적 측면에서 공감하기 어려운 장벽으로 다가온다. 특히 물리학이라는 신비의 섬 주변에는 수학이라는 낯선 언어의 장벽으로 둘러싸여 있어서 당최 이해할 수 없는 이질적인 대상으로 취급되거나 이해하기 어렵다는 사실이 물리학을 과도한 경외의 대상으로 만들...', '감성물리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '최무영 교수의 물리학 강의', '최무영', '자연과학', '물리학', '26,100', '책갈피', 1000, TO_DATE('2019년 01월', 'YYYY"년" MM"월"'), 1000, '이 책은 서울대학교 물리천문학부 최무영 교수가 자연과학을 전공하지 않는 학생들을 대상으로 한 강의를 바탕으로 만들어졌다. 과학의 진정한 의미를 누구나 쉽게 이해할 수 있도록 쓴 교양 물리학 입문서이면서도 고전역학, 양자역학, 상대성이론뿐 아니라 21세기의 최신 주제인 혼돈, 복잡계, 엔트로피, 우주의 탄생과 ...', '최무영 교수의 물리학 강의.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '프린키피아', '아이작 뉴턴 박병철', '자연과학', '물리학', '50,400', '휴머니스트', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '과학의 역사에서 단 한 권의 책을 고르라면 이 책일 수밖에 없다.과학 애독자의 성서, 뉴턴의 『프린키피아』 한국어판 출간!위대한 과학자 아이작 뉴턴의 『프린키피아』는 과학 혁명과 그로부터 이어진 근대 과학의 발전에 압도적인 공헌을 한 책이다. 고전역학의 바탕을 만들고 과학적 탐구 방법을 제시하며 현대 문...', '프린키피아.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '보이지 않아도 존재하고 있습니다', '김범준', '자연과학', '물리학', '16,200', '웅진지식하우스', 1000, TO_DATE('2022년 11월', 'YYYY"년" MM"월"'), 1000, '모든 것을 이루는 원자에서부터 모두가 ‘별의 먼지’인 인간까지,과학이라는 이름의 빛은 우리가 잊었던 경이로운 것들을 비춘다!『세상물정의 물리학』, 『관계의 과학』의 저자 김범준이 눈부신 과학의 언어로 써 내려간 42편의 삶의 송가* 국립과천과학관장 이정모, SF소설가 곽재식, 수학자 김민형, 우주물리학자 황정...', '보이지 않아도 존재하고 있습니다.jpg');
