INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '왼손잡이 우주', '최강신', '자연과학', '물리학', '14,400', '동아시아', 1000, TO_DATE('2022년 05월', 'YYYY"년" MM"월"'), 1000, '신이 왼손잡이라니!-볼프강 파울리, 노벨 물리학상 수상자왼쪽과 오른쪽을 구별하다 보면, 현대 물리학이 보인다!가장 독특하고 발칙한 글을 쓰는 물리학자한국고등과학원 박권 교수 강력 추천서울대학교 김진의 교수 추천 저자자네는 오른쪽을 설명할 수 있나?-영화 〈행복한 사전〉외계인에게 왼손을 설명할 수 ...', '왼손잡이 우주.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이토록 아름다운 물리학이라니', '에티엔 귀용 호세 비코 에티엔 레이삿 브누아 로만 박인규 박마래마리아', '자연과학', '물리학', '17,100', '미래의창', 1000, TO_DATE('2021년 04월', 'YYYY"년" MM"월"'), 1000, '일상에서 매일 접하는 아름다운 예술적 순간들 속에당신이 몰랐던 경이롭고 환상적인 과학 세계가 숨어 있다!1,000ft에 달하는 파리의 상징 에펠탑, 샴페인 잔 속에서 공기 방울들이 춤추듯 올라가는 모습, 씨앗이 바람을 타고 퍼져나가는 모습 등 우리 일상은 아름답고 우아한 순간으로 가득 차 있다. 이런 아름다움을 보...', '이토록 아름다운 물리학이라니.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '스핀', '이강영', '자연과학', '물리학', '19,800', '계단', 1000, TO_DATE('2018년 01월', 'YYYY"년" MM"월"'), 1000, '고전역학으로는 설명할 수도 이해할 수도 없는 진짜 양자역학, 스핀!화가가 선과 색으로 세상을 그리고 음악가가 소리로 우주를 표현하듯, 물리학자는 물리학 법칙을 통해 세계를 건설한다. 이 세상을 지금 우리가 보는 모습으로 만드는 물리학 법칙은 무엇일까? 우주를 가장 근본적인 수준에서 설명하는 이론은 양자역학이...', '스핀.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '이런 물리라면 포기하지 않을 텐데', '이광조', '자연과학', '물리학', '14,220', '보누스', 1000, TO_DATE('2021년 12월', 'YYYY"년" MM"월"'), 1000, '‘물리가 진짜 내 것이 되는’ 경험을 만끽해보자. 기호와 공식에 주눅들 걱정도, 그럴 필요도 없다. 이 책에서는 오로지 사칙연산(+, -, ×, ÷)만 사용해 물리의 핵심인 역학을 쉽고 명쾌하게 설명한다. 물리의 모든 개념을 머리에 욱여넣기보다는, 앞으로 어떤 물리 개념을 접해도 이해할 수 있도록 물리의 기초와 원리를 ...', '이런 물리라면 포기하지 않을 텐데.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '양자 컴퓨터 원리와 수학적 기초', '크리스 베른하트 이정문', '자연과학', '물리학', '22,500', '에이콘출판사', 1000, TO_DATE('2020년 08월', 'YYYY"년" MM"월"'), 1000, '기본적인 수학 배경지식을 갖춘 독자를 대상으로 양자 컴퓨터의 기본 원리를 설명하는 이론서다. 얽힘, 중첩 등 양자 컴퓨터의 바탕이 되는 양자역학적 개념의 수학적 모델을 가급적 단순화해서 설명하므로, 고등학교 수준의 수학 지식이 있으면 내용을 따라갈 수 있다. 양자 컴퓨터의 수학적 기초를 쌓고자 하는 독자에게 ...', '양자 컴퓨터 원리와 수학적 기초.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '열역학', '스티븐 베리 신석민', '자연과학', '물리학', '13,320', '김영사', 1000, TO_DATE('2021년 03월', 'YYYY"년" MM"월"'), 1000, '결코 흔들리지 않을 유일하고 보편적인 이론열역학의 기본개념에서 활용 및 발전사까지, 손에 잡히는 열역학 개론서세계적인 물리화학자 스티븐 베리가 대중을 상대로 쓴 열역학 개론서. 열역학은 열과 일, 에너지와 엔트로피를 다루는 과학으로, 결코 흔들리지 않을 유일하고 보편적인 이론이라는 아인슈타인의 말...', '열역학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '마음의 미래', '미치오 카쿠 박병철', '자연과학', '물리학', '21,600', '김영사', 1000, TO_DATE('2015년 04월', 'YYYY"년" MM"월"'), 1000, '의식세계의 가장 깊은 비밀이 풀리고 있다. 인간의 미래는 상상 그 이상이 될 것이다!뉴욕타임스 베스트셀러 1위! ≪평행우주≫ 미치오 카쿠의 최신작!이론물리학계의 세계적 석학, 궁극의 미래학자, 과학 엔터테이너 미치오 카쿠가 공개하는 우주에 존재하는 가장 큰 미스터리, 인간의 마음과 뇌에 관한 독보적 탐사. 두뇌...', '마음의 미래.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '처음 읽는 양자컴퓨터 이야기', '다케다 ??타로 전종훈 김재완', '자연과학', '물리학', '14,850', '플루토', 1000, TO_DATE('2021년 11월', 'YYYY"년" MM"월"'), 1000, '젊은 양자컴퓨터 개발자 중에서 가장 빛나는 연구자가 쓴 획기적인 책.양자컴퓨터의 본질을 보여준다!세계 최초로 양자 텔레포테이션 실현, 광 양자컴퓨터의 대가도쿄대학교 후루사와 아키라 교수이 책의 저자인 다케다 ??타로 교수는 도쿄대학교에서 빛의 양자물리학인 양자광학에 기반한 양자컴퓨터를 연구하고 있...', '처음 읽는 양자컴퓨터 이야기.jpg');
