INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파란하늘 빨간지구', '조천호', '자연과학', '지구과학', '14,400', '동아시아', 1000, TO_DATE('2019년 03월', 'YYYY"년" MM"월"'), 1000, '미세먼지와 기상 이변이 일상이 된 시대, 우리는 이런 기후 환경에서 살 수 있을까? 조천호 전 국립기상과학원 원장이 일반인의 눈높이에서 기후변화와 시대의 본질을 설명한다. 정치, 경제, 외교, 안보까지 모든 분야에서 변수가 된 기후변화에 대처하기 위한 인류의 미래에 대한 고민.', '파란하늘 빨간지구.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'NEWTON HIGHLIGHT 뉴턴 하이라이트 143 중고등학교 지구과학', '뉴턴프레스 다지카 에이이치 와다 스미오', '자연과학', '지구과학', '16,200', '(주)아이뉴턴', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '그림으로 지구과학의 개념을 배운다하늘과 바다, 그리고 풍요로운 대지가 있는 생명의 행성 ‘지구’. 우리 인간이 생활하는 지구와 우주의 활발한 변동에 대해 배우는 것이 ‘지구과학’이다. 21세기가 되어 환경 문제와 자원 에너지 문제, 기록적으로 지속되는 비, 초 대형 태풍 같은 엄청난 자연 재해 등 지구 환경의 변...', 'NEWTON HIGHLIGHT 뉴턴 하이라이트 143 중고등학교 지구과학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '기후위기와 탄소중립 수업 이야기', '한문정', '자연과학', '지구과학', '17,100', '우리학교', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '내일을 위한 작지만 확실한 발걸음,기후위기와 탄소중립 수업을 시작합니다개인의 실천을 넘어 사회적 해결책을 함께 찾는 아주 특별한 기후위기 수업과학교사이자 시민 활동가로 ‘변화를 꿈꾸는 과학기술인 네트워크(ESC)’와 ‘신나는 과학을 만드는 사람들’을 이끌어온 한문정 선생님이 교실에서 아이들과 함께한 기후...', '기후위기와 탄소중립 수업 이야기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '한 권으로 떠나는 세계 지형 탐사', '이우평', '자연과학', '지구과학', '34,200', '푸른숲', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '전 세계 다양한 지형을 담은 이 책은 우리 모두를 새로운 자연 학습의 장으로 인도한다. -정성훈, 대한지리학회 회장 그랜드캐니언, 아마존, 우유니 소금사막부터 하와이, 모뉴먼트밸리, 돌로미티까지 30년 차 지리 교사가 들려주는 전 세계의 아름답고 신비로운 지형 이야기 『한국 지형 산책』으로 수많은 독자에게 우리 ...', '한 권으로 떠나는 세계 지형 탐사.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '그린 쇼크', '최승신 윤대원 전지성', '자연과학', '지구과학', '19,800', '바른북스', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '유럽 에너지 위기의 전 세계 확산을 예견한 저자들의 정수가 담긴 책책장을 펼치는 순간 우리가 알고 있던 에너지 상식은 휴지통에 버려야 한다. 불편한 진실을 마주하는 순간 모두가 잘사는 에너지전환을 시작할 수 있다. 영국의 넷제로 정책과 독일의 탈원전·탈석탄이 한국의 전기와 가스요금을 올린다! 우리가 생존과 미...', '그린 쇼크.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '구름관찰자를 위한 가이드', '개빈 프레터피니 김성훈', '자연과학', '지구과학', '19,800', '김영사', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '만약 칼 세이건이 구름을 연구하는 기상학자였다면 『코스모스』 대신 바로 이 책을 썼을 것이다. - 지웅배(천문학자) 구름감상협회 공식 가이드북TEDGlobal 강연 130만 뷰[선데이 타임스] 베스트셀러(영국 20만 부 판매)오늘 당신의 하늘엔 어떤 구름이 떠 있나요?솜털 같은 구름 다발을 두둥실 띄워 놓은 적운(뭉게구름),...', '구름관찰자를 위한 가이드.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오리진', '루이스 다트넬 이충호', '자연과학', '지구과학', '18,000', '흐름출판', 1000, TO_DATE('2020년 09월', 'YYYY"년" MM"월"'), 1000, '세상은 어떻게 변모해왔고, 인류는 어떻게 세계를 지배하게 되었나?인류 기원과 진화의 해답을 찾기 위한 46억 년 역사의 장대한 여정!인류의 역사를 이야기할 때, 우리는 대부분 소수의 지도자와 집단의 대이동 그리고 결정적인 전쟁에 초점을 맞춘다. 하지만 여기에서 간과하는 것이 있다. 그것은 우리가 살아가고 있는 ...', '오리진.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'NEWTON HIGHLIGHT 뉴턴 하이라이트 최신 태양계 대도감', '', '자연과학', '지구과학', '18,000', '(주)아이뉴턴', 1000, TO_DATE('2017년 05월', 'YYYY"년" MM"월"'), 1000, '태양계에 행성만 있는 것은 아니다! 태양계의 핵심은 태양과 행성들이다. 태양계에서 태양의 영향력은 압도적이지만, 행성 말고도 그 ‘가족’은 대단히 다양하다. 위성, 왜소 행성, 소행성, 해왕성 궤도 통과 천체, 혜성, 그리고 다른 소천체들이 바로 그들이다. 태양계는 수없이 많은 상호간의 작용의 결과로 현재의 시스...', 'NEWTON HIGHLIGHT 뉴턴 하이라이트 최신 태양계 대도감.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '탄소로운 식탁', '윤지로', '자연과학', '지구과학', '16,200', '세종서적', 1000, TO_DATE('2022년 05월', 'YYYY"년" MM"월"'), 1000, '기후위기의 원인과 해결책을 알려면 식탁을 봐야 한다탄소중립과 먹거리를 엮어 취재한 최초의 책!연구가, 농부, 축산업자, 발전소 관계자, 환경운동가……곳곳의 목소리를 실시간으로 전하다 우리의 한 끼가 지구의 1도를 낮출 수 있다!기후변화를 넘어 기후위기 시대다. 탄소중립을 외치지만 지구온난화는 더 악화되...', '탄소로운 식탁.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '인류세: 인간의 시대', '최평순 EBS 다큐프라임 「인류세」 제작진', '자연과학', '지구과학', '14,850', '해나무', 1000, TO_DATE('2020년 09월', 'YYYY"년" MM"월"'), 1000, '지구의 정복자 인간. 우리는 어디서 왔는가, 우리는 무엇인가, 우리는 어디로 가는가?인간이 만든 새로운 지질시대, 인류세의 증거를 찾아 떠나는 여정.지구 곳곳의 인류세 현장을 누빈 다큐멘터리 제작진의 생생한 증언!너무나 강력해진 나머지 자기 자신을 포함한 지구 전체의 운명을 좌지우지하는 힘을 갖게 된 한 생물...', '인류세  인간의 시대.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'NEWTON HIGHLIGHT 145 지구 온난화 교과서', '뉴턴프레스', '자연과학', '지구과학', '16,200', '(주)아이뉴턴', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '2021년 8월 9일, 국제연합 산하 기관인 ‘기후 변화에 관한 정부간 협의체(IPCC)’의 제6차 평가 보고서가 공개되었다. 이것은 전 세계 과학자가 협력하여, 학술지에 게재된 논문에 입각해 기후 변화에 관한 최신 지식과 견해를 종합한 보고서이다. 2014년에 공개된 제5차 평가 보고서에서는 지구 온난화의 주된 원인은 ...', 'NEWTON HIGHLIGHT 145 지구 온난화 교과서.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '반드시 다가올 미래', '남성현', '자연과학', '지구과학', '16,020', '포르체', 1000, TO_DATE('2022년 12월', 'YYYY"년" MM"월"'), 1000, '〈JTBC 차이나는 클라스〉, 〈tvN 벌거벗은 세계사〉 출연!서울대학교 지구환경과학부 교수이자 기후과학자남성현 교수가 전하는 기후 비상 시대 지침서!이미 지구 평균 온도는 1도 올랐다기후 비상 시대, 우리가 알아야 할 기후 변화 필수 상식2022년 구글 국내 검색어 1위, ‘기후 변화’. 우리는 이미 이상 기온과 날씨 ...', '반드시 다가올 미래.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '2도가 오르기 전에', '남성현', '자연과학', '지구과학', '16,020', '애플북스', 1000, TO_DATE('2021년 10월', 'YYYY"년" MM"월"'), 1000, '하늘, 땅, 바다 그리고 얼음으로 알아보는 기후위기지구온난화로 인해 매년 바다에 흡수되는 열에너지 양은 지구상 모든 사람이 하루종일 전자레인지를 100개씩 가동할 때 소모되는 에너지양과 같다. 2020년 한 해 동안 바다에 흡수된 열에너지 양은 약 20ZJ로 추산되는데, 이것은 1초마다 원자폭탄이 4개씩 폭발하는 수준...', '2도가 오르기 전에.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '자연기반해법', '이우균 황석태 오일영 류필무 강부영', '자연과학', '지구과학', '16,200', '지을', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '기후위기 대응의 중심에 자연을 둔다면,희망은 있다.자연은 개발 대상이자 보호 대상이라는 오래된 인식과는 달리 우리는 아주 많은 부분을 자연에 기대어 살아간다. 실제로 전 세계에서 GDP의 절반 이상을, 12억 개에 달하는 일자리를 자연에 의존한다. 하지만 자연에 대한 투자는 기후변화에 대한 투자의 23%에 불과하다....', '자연기반해법.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '날마다 구름 한 점', '개빈 프레터피니 김성훈', '자연과학', '지구과학', '19,800', '김영사', 1000, TO_DATE('2021년 01월', 'YYYY"년" MM"월"'), 1000, '구름 좋아하세요?365장의 멋진 구름 사진과 함께하는 과학적인 멍때리기- 왕립학회 과학도서상, 왕립기상학회 마이클 헌트상 수상 작가 - TEDGlobal 130만뷰 강연구름감상협회 전 세계 5만 3천여 회원이 보내온 사진에서 엄선한 365장의 하늘 이미지에, 구름의 생성원리와 광학현상에 대한 친절한 설명, 문학작품에서 가려...', '날마다 구름 한 점.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '지구의 짧은 역사', '앤드루 H. 놀 이한음', '자연과학', '지구과학', '16,200', '다산사이언스', 1000, TO_DATE('2021년 10월', 'YYYY"년" MM"월"'), 1000, '일상의 언어로 만나는 지구의 역사!하버드 자연사 강의가 지금 내 손안에서 펼쳐진다!40년간 하버드에서 지구과학 연구를 이끌어온 저자, 앤드루 H. 놀이 기나긴 지구의 역사를 보기 쉽게 압축한 자연사 입문서. 저자는 지구가 인류의 요람이자 계속해서 살아갈 미래의 터전임에도 대다수가 관심을 두지 않는 현 상황에...', '지구의 짧은 역사.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '지구를 살리는 기후위기 수업', '이영경', '자연과학', '지구과학', '16,150', '한언', 1000, TO_DATE('2022년 01월', 'YYYY"년" MM"월"'), 1000, '기후위기에 맞서서 미래를 바꿀 지혜와 행동알고 나면 많은 것이 달라질 거야!지구 온도 상승, 뭐가 문제예요? 전기차 타면 기후위기 해결할 수 있지 않나요? 에코백과 텀블러 열심히 쓰면 되지 않아요? 기후위기, 기후변화와 같은 말을 들을 때마다 누구나 한 번쯤 가지는 의문이다. 하지만 이 문제가 얼...', '지구를 살리는 기후위기 수업.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '한 권으로 이해하는 하늘의 과학', '시라토리 케이 곽범신', '자연과학', '지구과학', '15,300', '시그마북스', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '과학을 이해하면하늘이 보인다!『한 권으로 이해하는 하늘의 과학』은 소형 비행기 조종사인 저자가 피부로 느낀 ‘하늘의 입체적인 모습’을 제시하며 공기역학을 비롯한 다양한 기상현상에 대해 설명한 책이다. 우리가 지상에서 올려다보는 저 하늘은 드라마틱한 변화로 가득한 공간이다. 상승기류와 하강기류, 동서로 흐...', '한 권으로 이해하는 하늘의 과학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '화이트 스카이', '엘리자베스 콜버트 김보영', '자연과학', '지구과학', '16,200', '쌤앤파커스', 1000, TO_DATE('2022년 09월', 'YYYY"년" MM"월"'), 1000, '115년 만의 폭우로 물에 잠긴 강남, 폭염으로 46도를 기록한 유럽…어쩌면 우리에게 남은 시간이 얼마 없을지도 모른다이제는 하늘이 하얗게 될지언정 살아남아야 한다퓰리처상 수상작『여섯 번째 대멸종』 엘리자베스 콜버트 신작강남을 물바다로 만든 115년 만에 발생한 최악의 폭우, 46도라는 믿을 수 없는 기온을 기록...', '화이트 스카이.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '원소란 무엇인가', '원소의 모든 것 편집실 김승훈 박세정', '자연과학', '지구과학', '19,000', '북스타', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '우리가 매일 사용하는 생활 속 일상용품을 비롯해 생물, 바다, 산, 지구, 별, 광대한 우주까지 모두 ‘원소’로 이뤄져 있다. 수소나 산소, 질소, 금, 은, 구리, 철 등 총 118종류이다. 이 책은 사진이나 일러스트를 통해 118개의 원소들이 저마다 갖고 있는 개성적이고 훌륭한 역할과 기능을 알기 쉽게 설명하고 있다. 꼭 ...', '원소란 무엇인가.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '위기의 지구, 물러설 곳 없는 인간', '남성현', '자연과학', '지구과학', '16,200', '21세기북스', 1000, TO_DATE('2020년 06월', 'YYYY"년" MM"월"'), 1000, '서울대 가지 않아도 들을 수 있는 명강의, ‘서가명강’지구의 시계가 멈춘다면? 위기의 지구를 구하는 과학 특강!대한민국 최고의 명품 강의를 책으로 만난다! 현직 서울대 교수진의 강의를 엄선한 ‘서가명강(서울대 가지 않아도 들을 수 있는 명강의)’ 시리즈의 열한 번째 책이 출간됐다. 역사, 철학, 과학, 의학, 예술...', '위기의 지구  물러설 곳 없는 인간.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '가이아', '제임스 러브록 홍욱희', '자연과학', '지구과학', '15,750', '갈라파고스', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '기후위기의 시대, 다시 새롭게 부상하는 『가이아』지구와 생물권에 관한 패러다임을 완전히 바꾼 지구과학 최고의 고전현대의 고전으로 자리매김한 제임스 러브록의 『가이아』가 저자의 1주기를 앞두고 개정증보판으로 출간되었다. 러브록이 제창한 가이아 이론의 시작점인 이 책은 1979년 초판 출간 당시 지구에 대한 기...', '가이아.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '녹색 계급의 출현', '브뤼노 라투르 니콜라이 슐츠 김지윤 김홍중 이현정 김환석 이규현', '자연과학', '지구과학', '13,500', '이음', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '이 책은 한계에 다다른 환경문제를 실질적으로 해결하기 위해 생태적 가치를 정치적, 제도적으로 실천하는 ‘생태화의 정치’가 필요하며, 그 실천을 주도하는 세력으로 ‘녹색 계급’이 출현해야 함을 제시한다. 환경문제는 지구에 거주하는 모든 이들, 즉 지구생활자에게 상관있는 문제이니만큼 녹색 계급은 이미 다수파...', '녹색 계급의 출현.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '기후학', '이승호', '자연과학', '지구과학', '31,500', '푸른길', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '기후학의 권위자 이승호 교수의 『기후학』 제3판이 출간되었다. 저자는 그의 첫 기후학 이론서 『기후학의 기초』를 출간한 지 약 20년 만에 기후학을 마무리하는 심정으로 『기후학』의 세 번째 개정 작업을 했다고 한다. 기후에 관한 관심은 기후학을 공부하는 학생, 관련 연구자만의 몫이 아니다. ‘기상관측 이래’, ...', '기후학.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, 'NEWTON HIGHLIGHT 뉴턴 하이라이트 133 날씨와 기상', '뉴턴프레스', '자연과학', '지구과학', '16,200', '(주)아이뉴턴', 1000, TO_DATE('2019년 11월', 'YYYY"년" MM"월"'), 1000, '날씨와 기상은 어떻게 결정되는가?‘날씨’는 우리의 일상생활은 물론 산업 전반에도 큰 영향을 미친다. 아침에 일어나서, 또는 며칠 전부터 일기 예보를 점검하는 일은 이제는 우리의 일상적인 활동이 되었다. 더구나 큰 눈이 내리거나 태풍이 다가오기라도 하면 우리의 생활은 더욱 큰 영향을 받는다. 뿐만 아니라 산업 ...', 'NEWTON HIGHLIGHT 뉴턴 하이라이트 133 날씨와 기상.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '브레이킹 바운더리스', '요한 록스트룀 오웬 가프니 전병옥', '자연과학', '지구과학', '19,800', '사이언스북스', 1000, TO_DATE('2022년 08월', 'YYYY"년" MM"월"'), 1000, '그레타 툰베리, 데이비드 애튼버러, 조천호 추천넷플릭스 「브레이킹 바운더리: 지구의 과학」 원작매년 기록적인 폭염과 폭우가 반복되는 현상이 전 세계적으로 심화하고 있다. 기후 위기 문제는 처음 밝혀진 이후부터 오랫동안 논쟁거리였다. 금성 대기의 온실 효과를 발견한 칼 세이건부터 2021년 노벨 물리학상을 기후 ...', '브레이킹 바운더리스.jpg');
