INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '뉴욕을 먹다', '김한송', '역사', '서양사/서양문화', '16,200', '따비', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '세계의 중심, 뉴욕을 만끽하는 또 하나의 방법다음 중 국가와 그 수도가 잘못 이어진 것은?이라는 문제에 여전히 단골로 등장하는 도시 뉴욕. 미국이라는 나라의 수도는 워싱턴DC이지만, 뉴욕이 미국 경제와 문화의 중심이라는 데 이의를 제기하는 사람은 거의 없을 것이다. 아니, 뉴욕은 미국이라는 한 국가를 넘어 세...', '뉴욕을 먹다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '영국 귀족의 영애', '무라카미 리코 문성호', '역사', '서양사/서양문화', '17,820', 'AK(에이케이 커뮤니케이션즈)', 1000, TO_DATE('2023년 01월', 'YYYY"년" MM"월"'), 1000, '트리비아의 인기 시리즈인 영국 시리즈의 일곱번째!사교계 데뷔부터 로맨스, 결혼까지,영애들의 인생을 따라가본다!영화를 누렸던 19세기~20세기 초의 영국. 웅장한 대저택, 호화로운 드레스, 사냥과 무도회 등, 당시 귀족들의 영애가 누렸던 풍요롭고 화려한 일상과 그 이면의 현실을 귀중한 자료를 통해 알아본다.', '영국 귀족의 영애.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '하룻밤에 읽는 영국사', '안병억', '역사', '서양사/서양문화', '16,200', '페이퍼로드', 1000, TO_DATE('2020년 07월', 'YYYY"년" MM"월"'), 1000, '이 책은 카이사르의 브리튼 침공부터 브렉시트와 코로나19가 등장하는 오늘날까지 영국의 역사를 다룬다. 영국인에게 세계사는 곧 영국의 역사다. 영국인의 생각만이 아니라 실제로도 그러했다. 카이사르의 브리튼 섬 원정 이후 역사시대에 들어선 뒤부터, 영국의 역사는 곧 유럽의 역사이고, 유럽이 세계를 지배하던 시절...', '하룻밤에 읽는 영국사.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '도시여행자를 위한 파리x역사', '주경철', '역사', '서양사/서양문화', '17,100', '휴머니스트', 1000, TO_DATE('2019년 12월', 'YYYY"년" MM"월"'), 1000, '지적인 여행을 즐기는 도시여행자에게 서양사학자 주경철이 들려주는 파리의 시간들역사와 여행이 만나다 ―역사학자 주경철의 도시 여행에 맞춤한 첫 역사책복잡한 서양사를 흥미롭고 명쾌하게 들려주는 ‘근대를 읽어주는 역사 스토리텔러’ 주경철 교수가 도시의 깊은 맛을 즐기는 도시여행자들을 유럽 문명의 중심지인 ...', '도시여행자를 위한 파리x역사.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '난 여자가 아닙니까?', '벨 훅스 김보명 노지양', '역사', '서양사/서양문화', '16,200', '동녘', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '인종주의를 이야기할 때 읽어야 할 가장 첫 번째 책!흑인 여성, 반인종주의자, 반제국주의자, 벨 훅스의 눈으로 본 미국사2023년 개봉한 디즈니의 영화 〈인어공주〉를 둘러싸고 영화계 안팎의 여론이 뜨겁다. 지금껏 수많은 매체에서 백인 여성의 모습으로 재현돼온 인어공주를 흑인 여성 배우가 연기한 까닭이다. 사람들...', '난 여자가 아닙니까 .jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '영국 귀족의 생활', '다나카 료조 김상호', '역사', '서양사/서양문화', '12,600', 'AK(에이케이 커뮤니케이션즈)', 1000, TO_DATE('2016년 04월', 'YYYY"년" MM"월"'), 1000, '아름다운 사진과 함께 영국 귀족의 우아한 삶을 조명한다‘귀족’이라는 단어를 들었을 때 가장 먼저 생각나는 나라, 영국. 영국은 현대에도 귀족제도가 남아있는 몇 안 되는 나라 중의 하나이다. 귀족이 영국 사회에서 어떠한 의미를 가지고 또 기능하는지, 상세한 설명과 아름다운 사진자료를 통하여, 그 위치와 실제 생...', '영국 귀족의 생활.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '어떤 선택의 재검토', '말콤 글래드웰 이영래', '역사', '서양사/서양문화', '14,220', '김영사', 1000, TO_DATE('2022년 04월', 'YYYY"년" MM"월"'), 1000, '『타인의 해석』 『아웃라이어』 『티핑 포인트』 글로벌 베스트셀러 저자 말콤 글래드웰제2차 세계대전 도쿄 대공습 민간인 학살을 재검토하다최고의 베스트셀러 저술가이자 천부적 이야기꾼 말콤 글래드웰이 제2차 세계대전의 결정적 순간을 다룬 역사 논픽션으로 돌아왔다. 1945년 미군의 ‘도쿄 대공습’이라는 민간인 ...', '어떤 선택의 재검토.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '제국의 몰락과 후국의 미래', '황성환', '역사', '서양사/서양문화', '25,200', '민플러스', 1000, TO_DATE('2021년 03월', 'YYYY"년" MM"월"'), 1000, '격변의 시대, 꼭 알아야 할 미국의 실체!간계와 폭력의 역사 제국주의 미국을 전면 해부하다. 2020년 코비드-19 감염병은 많은 것을 바꾸어 놓았다. 세상을 코로나19를 계기로 공포스러운 전염병을 본 것이 아니라 공포영화에 나오는 미 제국의 민낯을 보았다. 심각한 경제봉쇄와 곳곳에서 벌어진 폭동사태, 푸드뱅크 앞에 ...', '제국의 몰락과 후국의 미래.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '로마인 이야기 2', '시오노 나나미 김석희', '역사', '서양사/서양문화', '16,650', '한길사', 1000, TO_DATE('1995년 09월', 'YYYY"년" MM"월"'), 1000, '현대인의 삶의 철학과 좌표를 제시하는 동양인이 쓴 서양사. 는 방대한 자료를 취재·정리해가면서 엮어간 거대한 로마 통사이면서 현대를 어떻게 살아가야 하는지에 대한 지혜를 가르쳐주는 훌륭한 지침서라 할 수 있을 것이다. 서양인에 의해 씌어진 서양서보다 이 는 서양의 역사에 대해 냉정한 판단을 내렸을지도 모른다...', '로마인 이야기 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '파리는 날마다 축제', '어네스트 밀러 헤밍웨이 주순애', '역사', '서양사/서양문화', '13,500', '이숲', 1000, TO_DATE('2012년 01월', 'YYYY"년" MM"월"'), 1000, '헤밍웨이의 젊은 시절 파리 체류기어니스트 헤밍웨이가 젊은 시절(1921~1926) 프랑스 파리에 거주하면서 경험한 이야기들을 모아놓은 회고록. 글쓰기에 대한 치열한 열정, 파리에 거주하던 예술가들과의 인연, 첫 부인 해들리와 아들 존과의 일상, 아름다운 파리의 풍경과 단골 카페에서 일어난 일화들이 감동적으로 펼쳐진...', '파리는 날마다 축제.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '유대인 이야기', '홍익희', '역사', '서양사/서양문화', '25,200', '행성B잎새', 1000, TO_DATE('2013년 01월', 'YYYY"년" MM"월"'), 1000, '세계 경제를 좌우하는 유대인의 내밀한 저력을 역사의 흐름 속에서 밝혀낸다이스라엘의 경제적 성과는 눈부시다. 세계 경제위기 가운데서도 최저의 실업률과 호황을 맞은 부동산 시장, GDP 대비 R&D 투자비율 세계 1위와 같은 지표를 보여주고 있다. 자원이 풍부한 것도 아니고, 연일 이슬람 국가들과의 전쟁 공포에 휩싸인...', '유대인 이야기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '처음 읽는 미국사', '전국역사교사모임', '역사', '서양사/서양문화', '18,900', '휴머니스트', 1000, TO_DATE('2018년 12월', 'YYYY"년" MM"월"'), 1000, '‘인종과 문화의 샐러드’, 미국의 역사는 어떤 모습일까? 아시아계 원주민과 유럽계 백인, 아프리카계 흑인과 세계 각지의 이민자의 이질적인 문화가 어우러지고 충돌하면서 만들어진 ‘다양성 속에 보편성을 품은 미국’의 탄생부터 현재까지를 한 권에 담았다. 특히 많은 사람의 생생한 이야기를 통해 미국의 역사와 우...', '처음 읽는 미국사.jpg');
