INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '응급실 소생실 레벨 원입니다', '이강용', '예술', '사진', '16,200', '클', 1000, TO_DATE('2023년 04월', 'YYYY"년" MM"월"'), 1000, '생사의 최전선에서 고군분투하는 응급실 사람들의 긴박한 순간들그 속에서 ‘사진 찍는 간호사’가 포착한 감동과 공감의 장면들‘레벨 원Level 1’은 응급 중증도 분류에서 가장 위급한 단계를 가리킨다. 이 책의 제목 응급실 소생실 레벨 원입니다는 심정지나 중증외상 환자 등 즉시 소생이 필요한 레벨 원 환자가 응...', '응급실 소생실 레벨 원입니다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '가장 밝은 검정으로', '류한경', '예술', '사진', '19,800', '한겨레출판', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '나는 자주 다시 태어나고 싶다. 엄마의 다리 사이로 처음 나온 날처럼 완전히 새로워지고 싶다. 이 소망에 강렬하게 사로잡힐 때마다 몸에 타투를 새긴다타투의 영원함에 기꺼이 마음을 내어주는 사람들시인, 래퍼, 배우, 사진가 등 동시대를 살아가는 창작자 10인의 내밀한 목소리를 기록한 국내 첫 타투 사진집젊은 ...', '가장 밝은 검정으로.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '사진은 스타일링', '디어무이', '예술', '사진', '27,000', '휴머니스트', 1000, TO_DATE('2021년 05월', 'YYYY"년" MM"월"'), 1000, 'SNS 감성 사진도 팔리는 제품 사진도, 시작은 ‘스타일링’으로!수천 명이 반한 화제의 클래스 [디어무이의 감성 사진 스타일링 클래스]를 책으로 만나보세요!평소 사진 찍는 감각이 없어서 고민이었다면 ‘스타일링’부터 시작해보자. 카메라를 잘 몰라도, 전문 장비나 스튜디오가 없어도 괜찮다. 정말 중요한 것은 스타일...', '사진은 스타일링.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '좋은 사진을 만드는 ZAKO의 여행 사진 잘 찍는 법', 'ZAKO', '예술', '사진', '19,800', '한빛미디어', 1000, TO_DATE('2018년 10월', 'YYYY"년" MM"월"'), 1000, '여행사진을 찍는다는 것은 세상에 있는 모든 것’을 찍는 사진가가 될 수 있다는 것!저자는 이 책을 집필하기 위해 오랫동안 간직해온 보물을 들쳐보았다. 그동안 모아 놓은 외장하드가 바로 그것이다. 필름카메라로 찍은 사진부터 130만 화소의 디지털카메라로 찍은 사진, 최신 스마트폰으로 찍은 사진까지, 그 수가 엄...', '좋은 사진을 만드는 ZAKO의 여행 사진 잘 찍는 법.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '사진, 찍는 것인가 만드는 것인가', '앤 셀린 제이거 박태희', '예술', '사진', '30,000', '미진사', 1000, TO_DATE('2008년 03월', 'YYYY"년" MM"월"'), 1000, '예술, 다큐멘터리, 패션과 광고, 인물, 차세대 주자들 및 대표적인 사진 관계 종사자들 큐레이터, 갤러리스트, 에이전시 디렉터, 편집장, 출판인과 저널리스트이자 비평가인 앤 셀린 제이거가 나눈 인터뷰를 담았다. 사진가의 길에 들어서게 된 계기, 사진에 대한 남다른 시각, 철학, 관심사, 그들이 사용하는 카메라의 종...', '사진  찍는 것인가 만드는 것인가.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '권학봉의 프로페셔널 사진조명 강의 2', '권학봉', '예술', '사진', '49,500', '황금부엉이', 1000, TO_DATE('2018년 07월', 'YYYY"년" MM"월"'), 1000, '저 사진 어떻게 찍었을까?역시 전문가들이라 다르군. 내가 사진을 아무리 좋아해도 어림없겠지? 광고에 나온 사진들은 근사하다. 가슴이 두근거릴 정도로 멋지다는 느낌이 들 때도 있지만 그뿐이다. 사진에 빠져 300, 400만 원짜리 캐논과 니콘 카메라를 구입하고, 그렇게도 탐나던 전문가용 70만 원짜리 스피드라...', '권학봉의 프로페셔널 사진조명 강의 2.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '신의 영혼 오로라', '권오철', '예술', '사진', '21,600', '씨네21북스', 1000, TO_DATE('2023년 01월', 'YYYY"년" MM"월"'), 1000, '일생에 꼭 한 번, 오로라!*지난 10년간 독자들이 선택한 오로라 분야 도서 1위**오로라 극대기를 맞아 전면개정판 출간*인간이 경험할 수 있는 지상 최고의 경이로움,오로라를 찾아 떠나는 캐나다 옐로나이프 여행평범한 회사원에서 NASA가 인정한 천체사진가가 되기까지, 권오철의 노하우를 집약한 국내 유일 오로라 ...', '신의 영혼 오로라.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '지속의 순간들', '제프 다이어 이정현', '예술', '사진', '19,800', '을유문화사', 1000, TO_DATE('2022년 03월', 'YYYY"년" MM"월"'), 1000, '멈춰 있던 순간들이 만나면, 삶은 지속된다스티글리츠, 워커 에번스, 도로시아 랭, 다이앤 아버스, 윌리엄 이글스턴 등 거장의 반열에 오른 사진가들이 공통적으로 찍은 인물, 구도, 풍경을 비교하고 논하는 사진 비평 에세이가 을유문화사에서 출간됐다. 저자인 제프 다이어는 미용실, 벤치, 손, 길, 표지판이 여러 사진에...', '지속의 순간들.jpg');
