INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '안도 다다오, 건축을 살다', '미야케 리이치 위정훈', '예술', '건축', '22,500', '사람의집', 1000, TO_DATE('2023년 03월', 'YYYY"년" MM"월"'), 1000, '안도 다다오를 이해하는 가장 방대한 평전 한국뿐 아니라 전 세계에서 〈가장 좋아하는 건축가〉로 손꼽히는 안도 다다오의 인생과 작품을 총망라한 새로운 평전 『안도 다다오, 건축을 살다』가 출간되었다. 물론 지금까지도 그에 관한 많은 책이 출판되었고, 안도 다다오 본인이 직접 쓴 자서전도 여럿 소개되었다. 그렇...', '안도 다다오  건축을 살다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '건축학교에서 배운 101가지', '매튜 프레더릭 장택수', '예술', '건축', '10,800', '동녘', 1000, TO_DATE('2008년 03월', 'YYYY"년" MM"월"'), 1000, '건축가 매튜 프레더릭이 건축과 학생이라면 누구나 한번쯤은 경험하고 고민한 일에 대한 해결책을 풀어놓았다. 첫 번째 항목으로 내놓은 선은 이렇게 그린다라는 설계의 가장 기초부터 건축가는 대기만성형이다라는 마지막 101번째 항목까지 건축학도들이 알아야 할 것을 하나하나 짚어가며 간단명료한 문장과 그림...', '건축학교에서 배운 101가지.jpg');
