INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '건축 스케치·투시도 쉽게 따라하기', '무라야마 류지 이은정 임도균', '예술', '건축', '14,400', '더숲', 1000, TO_DATE('2021년 05월', 'YYYY"년" MM"월"'), 1000, '초간단! 스케치, 투시도, 건축 도면 그리고 채색까지 한 번에 끝내기컴퓨터 디자인도 손을 사용하는 스케치 능력에 기초한다건축은 더 이상 전문가들만의 영역이 아니다. 내 집 짓기와 건축 탐구에 대한 방송 프로그램들이 끊임없이 생겨나고 사람들의 관심은 이어지고 있다. 분야에 상관없이 다양한 일에 종사하는 사람들...', '건축 스케치 투시도 쉽게 따라하기.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나무, 돌, 그리고 한국 건축 문명', '전봉희', '예술', '건축', '22,500', '21세기북스', 1000, TO_DATE('2021년 08월', 'YYYY"년" MM"월"'), 1000, '한국 건축 문명은 어떻게 발생했고 어디로 가고 있을까?서울대 건축학과 교수가 들려주는 한국 건축의 진화와 미래왜 한국의 전통 건축은 나무로 만들어졌는가. 왜 오늘날 한국의 주거 형태는 아파트로 대표되는가. 『나무, 돌, 그리고 한국 건축 문명』은 30년 넘게 건축역사학을 연구해온 서울대학교 건축학과 전봉희 교...', '나무  돌  그리고 한국 건축 문명.jpg');
