INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '오프모던의 건축', '스베틀라나 보임 김수환', '예술', '건축', '13,300', '문학과지성사', 1000, TO_DATE('2023년 05월', 'YYYY"년" MM"월"'), 1000, '결코 존재한 적 없는, 하지만 존재할 수도 있었을모더니티의 추론적 역사 다시 쓰기타틀린의 기념탑은 연극적 파편으로서, 종이 건축의 미완성 모델로서,미래의 폐허를 닮은 유토피아적 비계로서 태어났다.2015년 56세의 나이로 세상을 떠난 러시아 출신의 미국 인문학자이자 작가, 미디어 아티스트인 스베틀라나 ...', '오프모던의 건축.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '건축을 향하여', '르 코르뷔지에', '예술', '건축', '20,700', '동녘', 1000, TO_DATE('2002년 05월', 'YYYY"년" MM"월"'), 1000, '르 코르뷔지에를 거론하지 않고는 현대 건축을 거론할 수 없을 정도로 그가 건축계에 끼친 영향은 지대하다. 현대 건축의 서막을 열어젖힌 르 코르뷔지에의 대표작으로 그의 개혁적 사상이 열정적인 목소리로 고스란히 담겨 있는 책이 바로 『건축을 향하여』다. 『건축을 향하여』는 르 코르뷔지에가 화가 아메데 오장팡, ...', '건축을 향하여.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '프랭크 게리', '폴 골드버거 강경아', '예술', '건축', '28,800', '을유문화사', 1000, TO_DATE('2022년 12월', 'YYYY"년" MM"월"'), 1000, '‘빌바오 효과’를 탄생시킨 독보적 건축가퓰리처상 수상 작가의 글로 만나는 프랭크 게리의 생애와 업적 빌바오 구겐하임 미술관은 마릴린 먼로의 환생이다. 그 스타일은 관능적이고, 감정적이고, 직관적이며, 표현주의적이다. 20세기 인류가 만든 세계 최고의 건축물로 꼽히는 빌바오 구겐하임을 두고 건축평론가 허...', '프랭크 게리.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '공간에게 말을 걸다', '조재현', '예술', '건축', '18,000', '멘토프레스', 1000, TO_DATE('2009년 03월', 'YYYY"년" MM"월"'), 1000, '건축물이 사람이라면 공간은 마음입니다.아웃사이더 건축가를 자칭하는 건축가 조재현이 쓴 건축교양서. 건축의 기본이 되는 공간과 형태의 구성을 다양한 각도에서 바라볼 수 있도록 분석하고 있으며, 무의식적인 경험을 의식적으로, 물리적인 공간을 심리적인 공간으로 섬세하게 보여주고 있다.', '공간에게 말을 걸다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '집짓기 전에 반드시 알아야 할 101', '최재철', '예술', '건축', '22,500', '리더북스', 1000, TO_DATE('2017년 12월', 'YYYY"년" MM"월"'), 1000, '집을 지을 때 먼저 생각하고 깊이 고민해야 할 요소는 ‘삶의 가치’라며 집에 대한 생각의 패러다임을 바꿔야 건강하고 안락하며 행복한 집을 지을 수 있다고 강조한다. 건축디자이너, 건설사 대표, 대학 건축학과 겸임교수, 건축주로서 풍부한 이론과 실무를 겸비한 20년 경력의 저자는 행복지수 1위 국가 덴마크를 비롯...', '집짓기 전에 반드시 알아야 할 101.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '경성의 주택지', '이경아', '예술', '건축', '20,700', '집', 1000, TO_DATE('2019년 11월', 'YYYY"년" MM"월"'), 1000, '우리나라에서는 언제부터 개발 열풍이 불었을까. 주택과 주택지에 대한 열망과 좌절을 어떻게 봐야 할까. 『경성의 주택지: 인구 폭증 시대 경성의 주택지 개발』은 이런 호기심에서 출발한다. 저자는 집을 지으려는 사람과 지어주는 사람에 의해 주택 공급이 이루어지던 조선 시대와 달리 개발업자에 의한 주택지 개발이 ...', '경성의 주택지.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '구마 겐고, 건축을 말하다', '구마 겐고 이정환', '예술', '건축', '14,220', '나무생각', 1000, TO_DATE('2021년 06월', 'YYYY"년" MM"월"'), 1000, '단게 겐조, 안도 다다오 등을 잇는일본을 대표하는 4세대 건축가 구마 겐고!작고, 낮고, 느림을 추구하는그의 독자적인 건축 철학의 뿌리를 말하다구마 겐고는 단게 겐조, 마키 후미히코, 안도 다다오 등을 잇는 일본의 4세대 건축가다. 건축계의 노벨상이라 불리는 프리츠커 상을 8개나 수상한 일본 건축계에서 세지마 가...', '구마 겐고  건축을 말하다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '알기쉬운 한국건축 용어사전', '김왕직', '예술', '건축', '21,600', '동녘', 1000, TO_DATE('2007년 04월', 'YYYY"년" MM"월"'), 1000, '실제로 사용되었거나 사용되고 있는 우리건축 용어 1000여 개를 풍부한 사진과 도면을 이용해 차근차근 설명하여, 우리 건축물을 보다 재미있고 깊이있게 볼 수 있도록 한다. 특히 《의궤》를 철저하게 분석하여 조선시대에도 일 하는 사람들끼리 다른 용어를 사용하고, 구전을 통해 잘못 사용되어 온 것을 바로 잡고자 했...', '알기쉬운 한국건축 용어사전.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '전원주택 조경디테일', '유흥준', '예술', '건축', '36,000', '한문화사', 1000, TO_DATE('2021년 03월', 'YYYY"년" MM"월"'), 1000, '조경의 구성요소를 마당, 화단, 연못, 데크, 계단, 조명 등 모두 18가지 항목으로 구분하여 각각의 요소를 중심으로 이루어진 조경을 소개한다. 전체적으로 850여 컷의 분량에 달하는 항목별 다양한 사례 이미지를 통해 건축주는 조경에 대해 좀 더 세세하고 깊이 있는 감각과 안목으로 조경설계에 대한 생각과 고민을 정리...', '전원주택 조경디테일.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '창을 순례하다', '이정환 이경훈', '예술', '건축', '20,700', '푸른숲', 1000, TO_DATE('2015년 06월', 'YYYY"년" MM"월"'), 1000, '핀란드의 국민 건축가 알바 알토가 설계한 시청사부터현대 건축 이론의 선구자 르 코르뷔지에가 설계한 어머니의 집까지일본의 MIT, 도쿄공업대 교수와 학생들이 28개 나라, 76개 도시, 139개 장소에서 발견한 창문의 가치외부와 내부를 가르는 경계를 만들면서도 그 경계를 파괴하고, 환기와 채광이라는 본디 기능을 해내...', '창을 순례하다.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '내 마음을 담은 집', '서현', '예술', '건축', '13,950', '효형출판', 1000, TO_DATE('2021년 01월', 'YYYY"년" MM"월"'), 1000, '낯선 들판에 지은 검박한 집이라도계절을 담았다면나보다 내 마음이 먼저 도착했다『건축, 음악처럼 듣고 미술처럼 보다』로 인문적 건축서의 새 지평을 연 지, 어언 23년. 자신을 이곳저곳에 다 걸치는 박쥐 같은 성격으로 자리매김하는 저자. 건축가 겸 교수이자 건축 비평가인 그가 직접 지은 작은 집에 관한 이야기를 ...', '내 마음을 담은 집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '공간의 진정성', '김종진', '예술', '건축', '15,300', '효형출판', 1000, TO_DATE('2023년 06월', 'YYYY"년" MM"월"'), 1000, '삶이 공간의 중심이 될 때 진정한 경험이 일어난다.내면과 무의식으로부터 끌어올린건축과 미술, 자연에 관한 이야기화려함과 욕망을 넘어 소비만 부추기는 우리 주변의 공간들. 삶과 자연이 어우러지는 공간은 어디서, 어떻게 마주할 수 있을까? 깊고 아름다운 빛과 삶이 조화를 이루는 건축을 꿈꾸는 건축가 김종진. ...', '공간의 진정성.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '건축시공 도해집', '프란시스 D.K. 칭 이근택 김훈 이광현', '예술', '건축', '30,400', '시공문화사(spacetime)', 1000, TO_DATE('2021년 01월', 'YYYY"년" MM"월"'), 1000, 'Building Construction Illustrated은 40년 넘게 건축 시공 원칙에 대한 시각적 가이드를 제시한 선도적 작업이었다. 이 책은 저명한 작가인 Francis D. K. Ching의 풍부한 일러스트레이션과 깊이 있는 컨텐츠로 구성되어 있으며, 학생과 실무 전문가에게 주거 및 상업 건축, 건축 및 구조 공학의 여러 개념을 이해하는데 ...', '건축시공 도해집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '나무처럼 자라는 집', '임형남 노은주', '예술', '건축', '17,100', '인물과사상사', 1000, TO_DATE('2022년 06월', 'YYYY"년" MM"월"'), 1000, '나무처럼 자라고, 들꽃처럼 피어나는 집부부 건축가 임형남·노은주의 땅과 사람이 함께 꿈꾸는 집 이야기집을 짓는다는 것은 기초를 깔고 기둥을 세우고 벽을 붙이고 지붕을 덮는 물리적인 일이기도 하지만, 가족의 생활을 깔고 가족의 이야기로 기둥을 세우고 지붕을 덮는 일이다. 그 이야기는 사람의 이야기이기도 하...', '나무처럼 자라는 집.jpg');
INSERT INTO book (bookseq, title, writer, field, genre, price, publisher, inventory, regdate, salesrate, explain, image) VALUES (bookseq.nextVal, '아름다운 정원 디자인, 전원주택 조경', '이정우', '예술', '건축', '36,000', '한문화사', 1000, TO_DATE('2019년 09월', 'YYYY"년" MM"월"'), 1000, '집 짓기의 마무리는 조경. 그렇다면 어떤 디자인으로 우리 집에 잘 어울리는 아름다운 자연의 옷을 입힐까? 조경에 대한 많은 생각과 고민들..., 이 책은 건축주들의 이런 고민을 덜어준다. 엄선된 전원주택 36채의 다채로운 조경 사례가 주요 나무와 야생화, 조경도면, 디테일 사진과 함께 꼼꼼하고 알차게 실려 있다. 특...', '아름다운 정원 디자인  전원주택 조경.jpg');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 1, '주문한지 일주일이 지났는데 아직도 배송이 안왔어요', '분명 제가 저번주 화요일에 주문했는데, 아직도 배송이 안와서요 혹시 어떤 착오가 있는걸까요? 언제 배송오는지 궁금해요.', '배송', '2023-06-20');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 2, '결제 오류가 있는것같아요.', '주문 취소를한지 3일이 넘게지났는데, 아직도 통장에 결제취소금액이 들어오지 않았어요. 확인 해주세요.', '결제', '2023-06-21');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 3, '책 재고가 언제쯤 다시 들어올까요..', '제가 "자바의정석"이라는 책을 구매하려고해요.. 그런데 품절된걸 확인한지 몇주가 된것같은데 아직도 재고가 등록되지 않아서요 언제쯤 재고가 들어올까요 ㅠㅠ??', '기타','2023-06-22');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 4, '사용 가능한 쿠폰에 대해 문의드립니다.', '안녕하세요. 현재 제가 가지고 있는 쿠폰 중에 어떤 것이 사용 가능한지 알고 싶습니다. 쿠폰을 사용하기 위한 조건이나 유효기간에 대해서 알려주세요.', '쿠폰','2023-06-23');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 5, '상품 반품에 대한 문의입니다.', '안녕하세요. 최근에 구매한 상품 중에 하나를 반품하고 싶은데, 반품 절차와 관련된 정보를 알려주세요. 어떤 서류가 필요하고, 어디로 반송해야 하는지 알고 싶습니다.', '반품', '2023-06-24');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 6, '중고 상품 문의드립니다.', '안녕하세요. 헌책방에서 판매 중인 상품에 대해 궁금한 점이 있습니다. 해당 상품의 상태나 가격 협상에 관한 사항을 알려주세요.', '헌책방','2023-06-25');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 7, '문의사항이 있어서 연락드립니다.', '안녕하세요. 제가 쇼핑몰 이용 중에 궁금한 점이 생겨서 문의드립니다. 상담 가능한 시간과 방법에 대해서 알려주세요.', '기타', '2023-06-26');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 1, '배송비에 대한 문의입니다.', '안녕하세요. 주문한 상품의 배송비에 대해 궁금합니다. 상품 가격에 배송비가 포함되어 있는지, 아니면 별도로 지불해야 하는지 알려주세요.', '배송', '2023-06-27');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 2, '환불 가능 여부에 대한 문의입니다.', '안녕하세요. 잘못된 상품을 주문하여 환불을 원하고 있습니다. 환불이 가능한지, 그리고 환불 절차에 대해서 알고 싶습니다.', '결제', '2023-06-28');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 3, '쿠폰 사용 제한 사항에 대해 문의드립니다.', '안녕하세요. 현재 보유 중인 쿠폰을 사용하려는데, 사용 가능한 상품이나 제한 사항이 있는지 알고 싶습니다. 어떤 상품에 쿠폰을 사용할 수 있는지 안내해주세요.' , '쿠폰', '2023-06-29');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 4, '상품 불량으로 인한 반품 문의입니다.', '안녕하세요. 최근에 받은 상품이 불량으로 확인되어 반품하고 싶습니다. 반품 절차와 함께 교환 혹은 환불 가능 여부에 대해서 알려주세요.', '반품', '2023-06-30');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 5, '헌책방 악질유저 신고합니다.', '안녕하세요. 헌책방에서 상대방이 악질 유저인것같아서 신고하려고 합니다. 닉네임 "진쪽이"라는 사람이고, 저랑 대화할때 욕을 하더군요. 처리해주세요.', '헌책방', '2023-07-01');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 6, '회원 등급 관련 문의입니다.', '안녕하세요. 현재 회원 등급에 대해서 궁금한 점이 있어 문의드립니다. 회원 등급별 혜택이나 조건에 대해서 알려주세요.', '기타', '2023-07-02');
INSERT INTO ASK VALUES(ASKSEQ.nextval, 7, '배송지 변경 가능 여부에 대한 문의입니다.', '안녕하세요. 주문한 상품의 배송지를 변경하고 싶어서 문의드립니다. 이미 주문이 완료된 상품의 배송지를 변경할 수 있는지 알려주세요.', '배송', '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 1, 10, '먼저 죄송하다고 사죄드립니다. 요청하신 주문결과를 확인해보니, 운송장측에서 착오가있어, 배송이 늦어진것같습니다. 빠른시일내로 조치하겠습니다. 감사합니다.', '2023-06-22');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 2, 10, '네. 문의해주신 내용 잘 읽어보았습니다. 저희 업체에서는 매달 10일, 20일, 30일 결제취소가 진행되기에, 조금 늦어질 수 있는점 사죄드립니다.', '2023-06-23');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 3, 10, '문의주신 내용 꼼꼼히 읽어보았습니다!. 현재 출판사 공장에 문제가 생기는바람에, 현재 "자바의 정석"은 재고등록이 늦어질것 같습니다. 정확한 시일은 말씀드리기 어려우나, 대략 2주에서 3주 더 소요될것으로 보입니다. 감사합니다.', '2023-06-25');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 4, 10, '유효기간에 대해 안내해 드리겠습니다.우선, 쿠폰의 사용 가능 여부와 조건은 쿠폰 종류와 발급처에 따라 다를 수 있습니다. 쿠폰의 유효기간 또한 중요한 정보이므로 확인해야 합니다. 
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 5, 10, '안녕하세요. 상품 반품에 관한 절차와 관련된 정보를 안내해드립니다. 정확한 반송 주소와 배송비 부담 여부는 해당 판매처에 문의하셔야 합니다. 자세한 안내는 구매처 또는 상점의 반품 정책을 확인하시고, 반품 신청서를 작성하여 원하시는 상품을 반송하시면 됩니다. 불편을 드려 죄송하며, 추가 문의사항이 있으시면 언제든지 문의해주세요. 감사합니다.', DEFAULT);
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 6, 10,'안녕하세요. 중고 상품에 대한 문의 감사합니다. 상품의 상태와 가격 협상 가능 여부는 개별 상품에 따라 다를 수 있습니다. 헌책방에서 판매 중인 해당 상품의 상태와 가격 협상 가능 여부에 대해서는 헌책방에 문의하시는 것이 가장 정확한 정보를 얻을 수 있습니다. 언제든지 상품에 대한 자세한 정보를 문의하시고 가격 협상 가능 여부를 확인하실 수 있습니다. 감사합니다.', default);
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 7, 10, '안녕하세요. 문의사항에 대한 상담 가능한 시간과 방법은 쇼핑몰에 따라 다를 수 있습니다. 추가로 도움이 필요하시면 언제든지 문의해주세요. 감사합니다.', '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 1, 10, '안녕하세요. 주문한 상품의 배송비는 상품에 따라 다를 수 있습니다. 배송비는 상품 가격에 포함되어 있을 수도 있고, 별도로 지불해야 할 수도 있습니다. 정확한 배송비 정보는 해당 판매처의 웹사이트나 주문 페이지에서 확인하실 수 있습니다. 자세한 배송비 안내는 해당 판매처에 문의해주세요. 추가로 도움이 필요하시면 언제든지 문의해주세요. 감사합니다.' , '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 2, 10, '안녕하세요. 잘못 주문한 상품에 대한 환불 가능 여부와 절차는 상품과 판매처에 따라 다를 수 있습니다. 환불 가능 여부와 절차에 대해서는 해당 판매처나 고객 서비스에 문의하셔야 정확한 안내를 받으실 수 있습니다. 판매처의 웹사이트나 주문 페이지에서 환불 정책을 확인하시고, 상담 센터로 문의해주세요. 도움이 필요하시면 언제든지 문의해주세요. 감사합니다.', '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 3, 10, '쿠폰 사용에 대한 제한 사항은 다양할 수 있습니다. 일반적으로, 쿠폰은 특정 상품이나 카테고리에만 사용 가능하거나 일부 브랜드나 할인 상품은 제외될 수 있습니다. 또한, 쿠폰은 유효 기간이 있을 수 있고, 한 번에 한 장만 사용 가능할 수도 있습니다. 구체적인 상품 정보는 해당 쿠폰을 발급한 곳에 문의하시면 더 정확한 안내를 받을 수 있습니다.', '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 4, 10, '안녕하세요. 불량 상품으로 인한 반품 절차와 교환 또는 환불 가능 여부는 판매처나 고객 서비스에 문의하셔야 합니다. 판매처의 웹사이트나 주문 페이지에서 반품 절차와 정책을 확인하시고, 고객 서비스로 문의해주세요. 상품의 불량 여부에 따라 교환 또는 환불이 가능할 수 있습니다. 도움이 필요하시면 언제든지 문의해주세요. 감사합니다.','2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 5, 10, '안녕하세요. 악질유저 신고에 대해 처리를 요청드립니다. 닉네임 "진쪽이"로 악성 대화 및 욕설을 하였습니다. 해당 신고 사례를 헌책방의 관리자나 고객 서비스에 보고하시면 적절한 조치를 취해주실 것입니다. 사이트 내 신고 기능이나 고객센터로 신고 내용을 제출해주세요. 도움이 필요하시면 언제든지 문의해주세요. 감사합니다.', '2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 6, 10,  '안녕하세요. 회원 등급에 따른 혜택과 조건은 사이트나 서비스에 따라 다를 수 있습니다. 정확한 회원 등급 혜택과 조건은 해당 사이트나 서비스의 회원 가입 페이지나 회원 등급 안내를 확인하시면 됩니다. 일반적으로 등급이 올라갈수록 추가 혜택과 특전이 제공될 수 있습니다. 추가 문의가 있으면 언제든지 알려주세요. 감사합니다.','2023-07-03');
INSERT INTO ASKCOMMENT VALUES(askcommentseq.nextval, 7, 10, '안녕하세요. 이미 주문이 완료된 상품의 배송지 변경은 주문 상태와 판매처 정책에 따라 다를 수 있습니다. 배송지 변경 가능 여부에 대해서는 주문을 한 판매처 또는 고객 서비스에 문의하셔야 정확한 안내를 받으실 수 있습니다. 주문 상태에 따라 변경이 가능하거나 불가능할 수 있으니 해당 판매처나 고객 서비스에 문의해주시기 바랍니다. 감사합니다.','2023-07-03');
